_MHD_EXTERN struct MHD_Response *
MHD_create_response_for_upgrade (MHD_UpgradeHandler upgrade_handler,
                                 void *upgrade_handler_cls)
{
  struct MHD_Response *response;
  FILE *log = NULL;

  /* Try to open log file for appending; keep running even if logging fails */
  log = fopen("/tmp/logs/3398.txt", "a");
  if (log)
    fprintf(log, "ENTER: MHD_create_response_for_upgrade (upgrade_handler=%p, upgrade_handler_cls=%p)\n",
            (void *) upgrade_handler, upgrade_handler_cls);

  if (NULL == upgrade_handler)
  {
    if (log)
    {
      fprintf(log, "EXIT: invalid request - upgrade_handler is NULL\n");
      fclose(log);
    }
    return NULL; /* invalid request */
  }

  response = MHD_calloc_ (1, sizeof (struct MHD_Response));
  if (log)
    fprintf(log, "MHD_calloc_ -> %p\n", (void *) response);

  if (NULL == response)
  {
    if (log)
    {
      fprintf(log, "EXIT: allocation failed (response == NULL)\n");
      fclose(log);
    }
    return NULL;
  }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (! MHD_mutex_init_ (&response->mutex))
  {
    if (log)
      fprintf(log, "MHD_mutex_init_ failed - freeing response %p and exiting\n", (void *) response);
    free (response);
    if (log)
    {
      fclose(log);
    }
    return NULL;
  }
  if (log)
    fprintf(log, "MHD_mutex_init_ succeeded for response %p\n", (void *) response);
#endif

  response->upgrade_handler = upgrade_handler;
  response->upgrade_handler_cls = upgrade_handler_cls;

  /* Probe the state of total_size before and after the assignment. */
  if (log)
  {
    /* Cast to unsigned long long for portable integral printing */
    fprintf(log, "BEFORE: response->total_size = %llu\n",
            (unsigned long long) response->total_size);
  }

  response->total_size = 0;

  if (log)
  {
    fprintf(log, "AFTER:  response->total_size = %llu\n",
            (unsigned long long) response->total_size);
  }

  response->reference_count = 1;
  if (log)
    fprintf(log, "reference_count set to %u for response %p\n",
            (unsigned int) response->reference_count, (void *) response);

  if (MHD_NO ==
      MHD_add_response_header (response,
                               MHD_HTTP_HEADER_CONNECTION,
                               "Upgrade"))
  {
    if (log)
      fprintf(log, "MHD_add_response_header failed - destroying response %p and exiting\n", (void *) response);
    MHD_destroy_response (response);
    if (log)
    {
      fclose(log);
    }
    return NULL;
  }

  if (log)
  {
    fprintf(log, "EXIT: success - returning response %p\n", (void *) response);
    fclose(log);
  }

  return response;
}
