_MHD_EXTERN struct MHD_Response *
MHD_create_response_for_upgrade (MHD_UpgradeHandler upgrade_handler,
                                 void *upgrade_handler_cls)
{
  struct MHD_Response *response;
  FILE *logf = fopen("/tmp/logs/3395.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER MHD_create_response_for_upgrade: upgrade_handler=%p, upgrade_handler_cls=%p\n",
            (void *)upgrade_handler, upgrade_handler_cls);
    fflush(logf);
  }

  if (NULL == upgrade_handler)
  {
    if (logf)
    {
      fprintf(logf, "EXIT: upgrade_handler is NULL -> returning NULL\n");
      fclose(logf);
    }
    return NULL; /* invalid request */
  }

  response = MHD_calloc_ (1, sizeof (struct MHD_Response));
  if (NULL == response)
  {
    if (logf)
    {
      fprintf(logf, "ALLOC FAIL: MHD_calloc_ returned NULL\n");
      fclose(logf);
    }
    return NULL;
  }

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (! MHD_mutex_init_ (&response->mutex))
  {
    if (logf)
    {
      fprintf(logf, "MUTEX INIT FAIL: response=%p, freeing and returning NULL\n", (void *)response);
      fclose(logf);
    }
    free (response);
    return NULL;
  }
#endif

  /* Assign handlers — keep original behavior. Log the assignment to capture the violation scenario. */
  response->upgrade_handler = upgrade_handler;
  response->upgrade_handler_cls = upgrade_handler_cls;

  if (logf)
  {
    fprintf(logf, "ASSIGN: response=%p, response->upgrade_handler=%p, response->upgrade_handler_cls=%p\n",
            (void *)response, (void *)response->upgrade_handler, response->upgrade_handler_cls);
    fflush(logf);
  }

  response->total_size = 0;
  response->reference_count = 1;
  if (MHD_NO ==
      MHD_add_response_header (response,
                               MHD_HTTP_HEADER_CONNECTION,
                               "Upgrade"))
  {
    if (logf)
    {
      fprintf(logf, "ADD_HEADER FAIL: destroying response %p and returning NULL\n", (void *)response);
      fclose(logf);
    }
    MHD_destroy_response (response);
    return NULL;
  }

  if (logf)
  {
    fprintf(logf, "SUCCESS: returning response=%p\n", (void *)response);
    fclose(logf);
  }

  return response;
}
