_MHD_EXTERN struct MHD_Response *
MHD_create_response_from_fd_at_offset64 (uint64_t size,
                                         int fd,
                                         uint64_t offset)
{
  struct MHD_Response *response;
  FILE *log = fopen("/tmp/logs/2895.txt", "a");

#if ! defined(HAVE___LSEEKI64) && ! defined(HAVE_LSEEK64)
  if ( (sizeof(uint64_t) > sizeof(off_t)) &&
       ( (size > (uint64_t) INT32_MAX) ||
         (offset > (uint64_t) INT32_MAX) ||
         ((size + offset) >= (uint64_t) INT32_MAX) ) )
  {
    if (log)
    {
      fprintf(log, "MHD_create_response_from_fd_at_offset64: entering with size=%llu, fd=%d, offset=%llu -- size/off overflow check FAILED, returning NULL\n",
              (unsigned long long) size, fd, (unsigned long long) offset);
      fclose(log);
    }
    return NULL;
  }
#endif
  if ( ((int64_t) size < 0) ||
       ((int64_t) offset < 0) ||
       ((int64_t) (size + offset) < 0) )
  {
    if (log)
    {
      fprintf(log, "MHD_create_response_from_fd_at_offset64: entering with size=%llu, fd=%d, offset=%llu -- negative check FAILED, returning NULL\n",
              (unsigned long long) size, fd, (unsigned long long) offset);
      fclose(log);
    }
    return NULL;
  }

  if (log)
  {
    fprintf(log, "MHD_create_response_from_fd_at_offset64: called with size=%llu, fd=%d, offset=%llu\n",
            (unsigned long long) size, fd, (unsigned long long) offset);
    fflush(log);
  }

  response = MHD_create_response_from_callback (size,
                                                MHD_FILE_READ_BLOCK_SIZE,
                                                &file_reader,
                                                NULL,
                                                &free_callback);
  if (NULL == response)
  {
    if (log)
    {
      fprintf(log, "MHD_create_response_from_fd_at_offset64: MHD_create_response_from_callback returned NULL\n");
      fclose(log);
    }
    return NULL;
  }

  /* Log state after creation */
  if (log)
  {
    /* Print pointer value and any known fields we will set */
    fprintf(log, "MHD_create_response_from_fd_at_offset64: response created at %p\n", (void *) response);
    /* Attempt to print existing crc_cls value if any (may be uninitialized previously) */
    fprintf(log, "MHD_create_response_from_fd_at_offset64: before assignment: response->crc_cls=%p\n",
            (void *) response->crc_cls);
    fflush(log);
  }

  response->fd = fd;
  response->is_pipe = false;
  response->fd_off = offset;

  /* Record the assignment that is relevant to MISRA C Rule 8.13 */
  if (log)
  {
    fprintf(log, "MHD_create_response_from_fd_at_offset64: assigning response->crc_cls = response; (response=%p)\n", (void *) response);
    fflush(log);
  }
  response->crc_cls = response;

  if (log)
  {
    fprintf(log, "MHD_create_response_from_fd_at_offset64: after assignment: response->crc_cls=%p, response->fd=%d, response->fd_off=%llu\n",
            (void *) response->crc_cls, response->fd, (unsigned long long) response->fd_off);
    fclose(log);
  }

  return response;
}
