_MHD_EXTERN enum MHD_Result
MHD_del_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t header_len;
  size_t content_len;
  FILE *f = NULL;

  /* Open log file for append. If this fails, continue without logging. */
  f = fopen("/tmp/logs/2384.txt", "a");
  if (f)
  {
    fprintf(f, "MHD_del_response_header: entry\n");
    fprintf(f, "  response=%p, header=%p, content=%p\n",
            (void *) response, (const void *) header, (const void *) content);
    fflush(f);
  }

  if ( (NULL == header) ||
       (NULL == content) )
  {
    if (f)
    {
      fprintf(f, "  early return: header or content is NULL\n");
      fclose(f);
    }
    return MHD_NO;
  }

  header_len = strlen (header);
  if (f)
  {
    fprintf(f, "  header_len=%zu\n", header_len);
    /* log a short hex preview of header (up to 32 bytes) */
    {
      size_t i, lim = header_len < 32u ? header_len : 32u;
      const unsigned char *p = (const unsigned char *) header;
      fprintf(f, "  header[0..%zu]=", lim ? (lim - 1) : 0);
      for (i = 0; i < lim; ++i)
        fprintf(f, "%02x", (unsigned) p[i]);
      fprintf(f, "\n");
      fflush(f);
    }
  }

  if ((0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR)) &&
      (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION) == header_len) &&
      MHD_str_equal_caseless_bin_n_ (header, MHD_HTTP_HEADER_CONNECTION,
                                     header_len))
  {
    if (f)
    {
      fprintf(f, "  connection header path matched -> calling del_response_header_connection\n");
      fflush(f);
    }
    enum MHD_Result _r = del_response_header_connection (response, content);
    if (f)
    {
      fprintf(f, "  del_response_header_connection returned %d\n", (int) _r);
      fclose(f);
    }
    return _r;
  }

  content_len = strlen (content);
  if (f)
  {
    fprintf(f, "  content_len=%zu\n", content_len);
    {
      size_t i, lim = content_len < 32u ? content_len : 32u;
      const unsigned char *p = (const unsigned char *) content;
      fprintf(f, "  content[0..%zu]=", lim ? (lim - 1) : 0);
      for (i = 0; i < lim; ++i)
        fprintf(f, "%02x", (unsigned) p[i]);
      fprintf(f, "\n");
      fflush(f);
    }
  }

  pos = response->first_header;
  if (f)
  {
    fprintf(f, "  start scanning headers: first_header=%p\n", (void *) pos);
    fflush(f);
  }
  while (NULL != pos)
  {
    if (f)
    {
      fprintf(f, "  examining pos=%p: header_size=%zu, value_size=%zu\n",
              (void *) pos, pos->header_size, pos->value_size);
      fflush(f);
    }

    if ((header_len == pos->header_size) &&
        (content_len == pos->value_size))
    {
      int header_cmp = memcmp (header, pos->header, header_len);
      if (f)
      {
        fprintf(f, "    lengths match: header_cmp=%d\n", header_cmp);
        /* hex preview of stored header/value (up to 32 bytes) */
        {
          size_t i, lim = pos->header_size < 32u ? pos->header_size : 32u;
          const unsigned char *p = (const unsigned char *) pos->header;
          fprintf(f, "    pos->header[0..%zu]=", lim ? (lim - 1) : 0);
          for (i = 0; i < lim; ++i)
            fprintf(f, "%02x", (unsigned) p[i]);
          fprintf(f, "\n");
        }
        fflush(f);
      }

      if (0 == header_cmp)
      {
        int content_cmp = memcmp (content, pos->value, content_len);
        if (f)
        {
          fprintf(f, "    header matched, content_cmp=%d\n", content_cmp);
          {
            size_t i, lim = pos->value_size < 32u ? pos->value_size : 32u;
            const unsigned char *p = (const unsigned char *) pos->value;
            fprintf(f, "    pos->value[0..%zu]=", lim ? (lim - 1) : 0);
            for (i = 0; i < lim; ++i)
              fprintf(f, "%02x", (unsigned) p[i]);
            fprintf(f, "\n");
          }
          fflush(f);
        }

        if (0 == content_cmp)
        {
          if (f)
          {
            fprintf(f, "    FULL MATCH found at pos=%p -> removing header and freeing memory\n", (void *) pos);
            fflush(f);
          }

          _MHD_remove_header (response, pos);
          if (f)
          {
            fprintf(f, "    after _MHD_remove_header: adjusting and freeing pos->header(%p) and pos->value(%p) and pos(%p)\n",
                    (void *) pos->header, (void *) pos->value, (void *) pos);
            fflush(f);
          }

          free (pos->header);
          free (pos->value);
          free (pos);

          if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
                header_len) &&
               MHD_str_equal_caseless_bin_n_ (header,
                                              MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                              header_len) )
            response->flags_auto &=
              ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_TRANS_ENC_CHUNKED);
          else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_DATE) ==
                     header_len) &&
                    MHD_str_equal_caseless_bin_n_ (header,
                                                   MHD_HTTP_HEADER_DATE,
                                                   header_len) )
            response->flags_auto &=
              ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_DATE_HDR);
          else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
                     header_len) &&
                    MHD_str_equal_caseless_bin_n_ (header,
                                                   MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                   header_len) )
          {
            if (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                     header_len))
              response->flags_auto &=
                ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_CONTENT_LENGTH);
          }

          if (f)
          {
            fprintf(f, "    final flags_auto=0x%08x\n", (unsigned) response->flags_auto);
            fclose(f);
          }
          return MHD_YES;
        }
      }
    }
    pos = pos->next;
    if (f)
    {
      fprintf(f, "  moving to next header: pos=%p\n", (void *) pos);
      fflush(f);
    }
  }

  if (f)
  {
    fprintf(f, "  no matching header found -> returning MHD_NO\n");
    fclose(f);
  }
  return MHD_NO;
}
