_MHD_EXTERN enum MHD_Result
MHD_del_response_header (struct MHD_Response *response,
                         const char *header,
                         const char *content)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t header_len;
  size_t content_len;
  FILE *log = fopen("/tmp/logs/2383.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_del_response_header: response=%p header=%p content=%p\n",
            (void *) response, (const void *) header, (const void *) content);
  }

  if ( (NULL == header) ||
       (NULL == content) )
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: NULL parameter: header=%p content=%p\n",
              (const void *) header, (const void *) content);
      fclose(log);
    }
    return MHD_NO;
  }

  header_len = strlen (header);
  if (log)
  {
    fprintf(log, "computed header_len=%zu\n", header_len);
    /* print up to 200 chars of header for diagnosis */
    fprintf(log, "header (up to 200): '%.*s'\n", (int) (header_len > 200 ? 200U : header_len), header);
  }

  if ((0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR)) &&
      (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION) == header_len) &&
      MHD_str_equal_caseless_bin_n_ (header, MHD_HTTP_HEADER_CONNECTION,
                                     header_len))
  {
    if (log)
      fprintf(log, "branch: connection header handler invoked\n");
    enum MHD_Result res = del_response_header_connection (response, content);
    if (log)
    {
      fprintf(log, "del_response_header_connection returned %d\n", (int) res);
      fclose(log);
    }
    return res;
  }

  content_len = strlen (content);
  if (log)
  {
    fprintf(log, "computed content_len=%zu\n", content_len);
    fprintf(log, "content (up to 200): '%.*s'\n", (int) (content_len > 200 ? 200U : content_len), content);
  }

  pos = response->first_header;
  if (log)
    fprintf(log, "starting header list traversal: first_header=%p\n", (void *) pos);

  while (NULL != pos)
  {
    if (log)
      fprintf(log, "visiting header node %p header_size=%zu value_size=%zu next=%p\n",
              (void *) pos, pos->header_size, pos->value_size, (void *) pos->next);

    if ((header_len == pos->header_size) &&
        (content_len == pos->value_size))
    {
      int header_cmp = 0;
      int content_cmp = 0;

      /* Only compare bytes when lengths match (as original did) to avoid extra reads */
      if (header_len > 0)
        header_cmp = memcmp (header, pos->header, header_len);
      if (content_len > 0)
        content_cmp = memcmp (content, pos->value, content_len);

      if (log)
        fprintf(log, "comparisons: header_cmp=%d content_cmp=%d\n", header_cmp, content_cmp);

      if ((0 == header_cmp) && (0 == content_cmp))
      {
        if (log)
          fprintf(log, "match found: removing header node %p\n", (void *) pos);

        _MHD_remove_header (response, pos);
        free (pos->header);
        free (pos->value);
        free (pos);

        if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
              header_len) &&
             MHD_str_equal_caseless_bin_n_ (header,
                                            MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                            header_len) )
        {
          if (log)
            fprintf(log, "clearing MHD_RAF_HAS_TRANS_ENC_CHUNKED flag\n");
          response->flags_auto &=
            ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_TRANS_ENC_CHUNKED);
        }
        else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_DATE) ==
                    header_len) &&
                  MHD_str_equal_caseless_bin_n_ (header,
                                                 MHD_HTTP_HEADER_DATE,
                                                 header_len) )
        {
          if (log)
            fprintf(log, "clearing MHD_RAF_HAS_DATE_HDR flag\n");
          response->flags_auto &=
            ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_DATE_HDR);
        }
        else if ( (MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
                    header_len) &&
                  MHD_str_equal_caseless_bin_n_ (header,
                                                 MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                 header_len) )
        {
          if (NULL == MHD_get_response_element_n_ (response,
                                                   MHD_HEADER_KIND,
                                                   MHD_HTTP_HEADER_CONTENT_LENGTH,
                                                   header_len))
          {
            if (log)
              fprintf(log, "clearing MHD_RAF_HAS_CONTENT_LENGTH flag\n");
            response->flags_auto &=
              ~((enum MHD_ResponseAutoFlags) MHD_RAF_HAS_CONTENT_LENGTH);
          }
          else
          {
            if (log)
              fprintf(log, "content-length header still present elsewhere; not clearing flag\n");
          }
        }

        if (log)
        {
          fprintf(log, "RETURN MHD_YES\n");
          fclose(log);
        }
        return MHD_YES;
      }
    }

    pos = pos->next;
  }

  if (log)
  {
    fprintf(log, "no matching header found: RETURN MHD_NO\n");
    fclose(log);
  }
  return MHD_NO;
}
