_MHD_EXTERN enum MHD_Result
MHD_digest_auth_calc_userdigest (enum MHD_DigestAuthAlgo3 algo3,
                                 const char *username,
                                 const char *realm,
                                 const char *password,
                                 void *userdigest_bin,
                                 size_t bin_buf_size)
{
  struct DigestAlgorithm da;
  enum MHD_Result ret;
  FILE *logf = fopen("/tmp/logs/2423.txt", "a");

  if (logf != NULL)
  {
    fprintf(logf, "ENTER MHD_digest_auth_calc_userdigest: algo3=%d, username=%p, realm=%p, password=%p, userdigest_bin=%p, bin_buf_size=%zu\n",
            (int)algo3, (const void *)username, (const void *)realm, (const void *)password, userdigest_bin, bin_buf_size);
    fflush(logf);
  }

  if (! digest_init_one_time (&da, get_base_digest_algo (algo3)))
  {
    if (logf != NULL)
    {
      fprintf(logf, "digest_init_one_time failed\n");
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;
  }

  /* Log the needed size before the original size check */
  {
    size_t needed_size = digest_get_size (&da);
    if (logf != NULL)
    {
      fprintf(logf, "digest_get_size -> %zu, bin_buf_size=%zu\n", needed_size, bin_buf_size);
      fflush(logf);
    }

    if (needed_size > bin_buf_size)
      ret = MHD_NO;
    else
    {
      /* Important: original code called strlen on username and realm without NULL
         checks. We log that we are about to do that so test runs can capture
         the pointers and the fact that strlen will be invoked on them. Do not
         perform any extra checks that would alter original behavior. */
      if (logf != NULL)
      {
        fprintf(logf, "About to call strlen on username=%p and realm=%p (no checks performed)\n",
                (const void *)username, (const void *)realm);
        fflush(logf);
      }

      /* Original behavior retained: may call strlen on NULL or on unterminated buffers. */
      calc_userdigest (&da,
                       username,
                       strlen (username),
                       realm,
                       strlen (realm),
                       password,
                       userdigest_bin);
      ret = MHD_YES;

#ifdef MHD_DIGEST_HAS_EXT_ERROR
      if (digest_ext_error (&da))
        ret = MHD_NO;
#endif /* MHD_DIGEST_HAS_EXT_ERROR */

      if (logf != NULL)
      {
        fprintf(logf, "calc_userdigest completed, provisional ret=%d\n", (int)ret);
        fflush(logf);
      }
    }
  }

  digest_deinit (&da);

  if (logf != NULL)
  {
    fprintf(logf, "EXIT MHD_digest_auth_calc_userdigest: ret=%d\n", (int)ret);
    fflush(logf);
    fclose(logf);
  }

  return ret;
}
