_MHD_EXTERN enum MHD_Result
MHD_digest_auth_calc_userhash (enum MHD_DigestAuthAlgo3 algo3,
                               const char *username,
                               const char *realm,
                               void *userhash_bin,
                               size_t bin_buf_size)
{
  struct DigestAlgorithm da;
  enum MHD_Result ret;
  FILE *log = fopen("/tmp/logs/2413.txt", "a");

  if (log)
    fprintf(log, "ENTER MHD_digest_auth_calc_userhash: algo3=%d, username=%p, realm=%p, userhash_bin=%p, bin_buf_size=%zu\n",
            (int) algo3, (const void *) username, (const void *) realm, (const void *) userhash_bin, bin_buf_size);

  if (! digest_init_one_time (&da, get_base_digest_algo (algo3)))
  {
    if (log)
      fprintf(log, "digest_init_one_time failed -> returning MHD_NO\n");
    if (log)
      fclose(log);
    return MHD_NO;
  }

  /* Check buffer size vs digest size and log details. */
  if (log)
    fprintf(log, "digest_get_size() = %zu, bin_buf_size = %zu\n",
            digest_get_size(&da), bin_buf_size);

  if (digest_get_size (&da) > bin_buf_size)
    ret = MHD_NO;
  else
  {
    size_t ulen = 0;
    size_t rlen = 0;

    if (log)
    {
      fprintf(log, "Preparing to compute username/realm lengths. username=%p, realm=%p\n",
              (const void *) username, (const void *) realm);
      if (username == NULL)
        fprintf(log, "NOTE: username is NULL (strlen will be invoked as in original code)\n");
      if (realm == NULL)
        fprintf(log, "NOTE: realm is NULL (strlen will be invoked as in original code)\n");
    }

    /* Preserve original behaviour: call strlen exactly as before. */
    ulen = strlen (username);
    rlen = strlen (realm);

    if (log)
      fprintf(log, "Computed lengths: ulen=%zu, rlen=%zu\n", ulen, rlen);

    calc_userhash (&da,
                   username,
                   ulen,
                   realm,
                   rlen,
                   userhash_bin);
    ret = MHD_YES;

#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (&da))
      ret = MHD_NO;
#endif /* MHD_DIGEST_HAS_EXT_ERROR */
  }

  digest_deinit (&da);

  if (log)
  {
    fprintf(log, "EXIT MHD_digest_auth_calc_userhash: ret=%d\n", (int) ret);
    fclose(log);
  }

  return ret;
}
