_MHD_EXTERN enum MHD_Result
MHD_digest_auth_calc_userhash (enum MHD_DigestAuthAlgo3 algo3,
                               const char *username,
                               const char *realm,
                               void *userhash_bin,
                               size_t bin_buf_size)
{
  struct DigestAlgorithm da;
  enum MHD_Result ret;
  FILE *log = fopen("/tmp/logs/2413.txt", "a");

  if (log) {
    fprintf(log, "ENTER: MHD_digest_auth_calc_userhash: algo3=%d, username=%p, realm=%p, userhash_bin=%p, bin_buf_size=%zu\n",
            (int)algo3, (const void *)username, (const void *)realm, userhash_bin, bin_buf_size);
    fflush(log);
  }

  if (! digest_init_one_time (&da, get_base_digest_algo (algo3))) {
    if (log) {
      fprintf(log, "digest_init_one_time failed\n");
      fclose(log);
    }
    return MHD_NO;
  }

  if (log) {
    size_t dsize = digest_get_size(&da);
    fprintf(log, "digest_get_size=%zu\n", dsize);
    fflush(log);
  }

  if (digest_get_size (&da) > bin_buf_size)
    ret = MHD_NO;
  else
  {
    if (log) {
      fprintf(log, "About to call strlen on username and realm. username=%p, realm=%p\n",
              (const void *)username, (const void *)realm);
      fprintf(log, "Attempting strlen(username)\n");
      fflush(log);
    }

    /* Preserve original behavior: compute lengths using strlen (may crash if realm == NULL or not NUL-terminated) */
    size_t username_len = strlen (username);
    if (log) {
      fprintf(log, "strlen(username)=%zu\n", username_len);
      fprintf(log, "Attempting strlen(realm)\n");
      fflush(log);
    }

    size_t realm_len = strlen (realm); /* Potential dereference of NULL or out-of-bounds read if realm is invalid */
    if (log) {
      fprintf(log, "strlen(realm)=%zu\n", realm_len);
      fflush(log);
    }

    calc_userhash (&da,
                   username,
                   username_len,
                   realm,
                   realm_len,
                   userhash_bin);
    ret = MHD_YES;

#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (&da))
      ret = MHD_NO;
#endif /* MHD_DIGEST_HAS_EXT_ERROR */
  }
  digest_deinit (&da);

  if (log) {
    fprintf(log, "EXIT: MHD_digest_auth_calc_userhash returning %d\n", (int)ret);
    fclose(log);
  }

  return ret;
}
