_MHD_EXTERN enum MHD_Result
MHD_digest_auth_calc_userhash (enum MHD_DigestAuthAlgo3 algo3,
                               const char *username,
                               const char *realm,
                               void *userhash_bin,
                               size_t bin_buf_size)
{
  struct DigestAlgorithm da;
  enum MHD_Result ret;

  /* Logging: open log file to record control flow and state. */
  {
    FILE *log = fopen("/tmp/logs/2413.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_digest_auth_calc_userhash ENTER: algo3=%d, username=%p, realm=%p, userhash_bin=%p, bin_buf_size=%zu\n",
              (int)algo3, (const void *)username, (const void *)realm, userhash_bin, bin_buf_size);
      fflush(log);
      fclose(log);
    }
  }

  if (! digest_init_one_time (&da, get_base_digest_algo (algo3)))
  {
    /* Log initialization failure */
    FILE *log = fopen("/tmp/logs/2413.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_digest_auth_calc_userhash: digest_init_one_time FAILED\n");
      fflush(log);
      fclose(log);
    }
    return MHD_NO;
  }

  /* Log digest size and buffer size comparison */
  {
    size_t dsize = digest_get_size (&da);
    FILE *log = fopen("/tmp/logs/2413.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_digest_auth_calc_userhash: digest_get_size=%zu, bin_buf_size=%zu\n",
              dsize, bin_buf_size);
      fflush(log);
      fclose(log);
    }
  }

  if (digest_get_size (&da) > bin_buf_size)
    ret = MHD_NO;
  else
  {
    /* Log just before calling strlen on username and realm. Do not call strlen here for logging
       to avoid altering the original behavior (which will call strlen below and may crash if
       username or realm are invalid). We record the pointer values and whether they are NULL. */
    {
      FILE *log = fopen("/tmp/logs/2413.txt", "a");
      if (log)
      {
        fprintf(log, "MHD_digest_auth_calc_userhash: about to call calc_userhash. username=%p, realm=%p\n",
                (const void *)username, (const void *)realm);
        fprintf(log, "MHD_digest_auth_calc_userhash: username_is_null=%d, realm_is_null=%d\n",
                (username == NULL) ? 1 : 0,
                (realm == NULL) ? 1 : 0);
        fflush(log);
        fclose(log);
      }
    }

    /* Original behavior: call calc_userhash using strlen() on username and realm. */
    calc_userhash (&da,
                   username,
                   strlen (username),
                   realm,
                   strlen (realm),
                   userhash_bin);
    ret = MHD_YES;

    /* Log successful call to calc_userhash and indicate ret so far. */
    {
      FILE *log = fopen("/tmp/logs/2413.txt", "a");
      if (log)
      {
        fprintf(log, "MHD_digest_auth_calc_userhash: calc_userhash called, provisional ret=MHD_YES\n");
        fflush(log);
        fclose(log);
      }
    }

#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (&da))
      ret = MHD_NO;
#endif /* MHD_DIGEST_HAS_EXT_ERROR */

    /* If extended error detection present, log the final decision after checking ext error. */
#ifdef MHD_DIGEST_HAS_EXT_ERROR
    {
      FILE *log = fopen("/tmp/logs/2413.txt", "a");
      if (log)
      {
        fprintf(log, "MHD_digest_auth_calc_userhash: digest_ext_error=%d, final ret=%d\n",
                digest_ext_error(&da) ? 1 : 0, (int)ret);
        fflush(log);
        fclose(log);
      }
    }
#endif
  }

  digest_deinit (&da);

  /* Final exit log. */
  {
    FILE *log = fopen("/tmp/logs/2413.txt", "a");
    if (log)
    {
      fprintf(log, "MHD_digest_auth_calc_userhash EXIT: ret=%d\n", (int)ret);
      fflush(log);
      fclose(log);
    }
  }

  return ret;
}
