_MHD_EXTERN char *
MHD_digest_auth_get_username (struct MHD_Connection *connection)
{
  const struct MHD_RqDAuth *params;
  char *username;
  size_t buf_size;
  enum MHD_DigestAuthUsernameType uname_type;

  /* Minimal entry log so callers/test harness can see this function was entered. */
  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: entered (connection=%p)\n", (void *) connection);
      fclose(log);
    }
  }

  params = MHD_get_rq_dauth_params_ (connection);
  if (NULL == params)
  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: params == NULL -> returning NULL\n");
      fclose(log);
    }
    return NULL;
  }

  uname_type = get_rq_uname_type (params);
  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: uname_type=%d\n", (int) uname_type);
      fclose(log);
    }
  }

  if ( (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD != uname_type) &&
       (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED != uname_type) )
  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: unsupported uname_type=%d -> returning NULL\n", (int) uname_type);
      fclose(log);
    }
    return NULL;
  }

  buf_size = get_rq_unames_size (params, uname_type);

  mhd_assert (0 != buf_size);

  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: buf_size=%zu\n", buf_size);
      fclose(log);
    }
  }

  username = (char *) MHD_calloc_ (1, buf_size);
  if (NULL == username)
  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: allocation failed (buf_size=%zu) -> returning NULL\n", buf_size);
      fclose(log);
    }
    return NULL;
  }

  /* The original code contains an invariant controlling expression: if (1).
     Log that we observed this invariant and then execute the original block
     verbatim so behavior is unchanged. */
  if (1)
  {
    struct MHD_DigestAuthUsernameInfo uname_strct;
    size_t used;

    {
      FILE *log = fopen("/tmp/logs/4359.txt", "a");
      if (log != NULL)
      {
        fprintf(log, "MHD_digest_auth_get_username: entering invariant if(1) block\n");
        fclose(log);
      }
    }

    memset (&uname_strct, 0, sizeof(uname_strct));

    used = get_rq_uname (params, uname_type, &uname_strct,
                         (uint8_t *) username, buf_size);

    {
      FILE *log = fopen("/tmp/logs/4359.txt", "a");
      if (log != NULL)
      {
        fprintf(log, "MHD_digest_auth_get_username: get_rq_uname used=%zu, uname_strct.uname_type=%d\n",
                used, (int) uname_strct.uname_type);
        fclose(log);
      }
    }

    if (uname_type != uname_strct.uname_type)
    { /* Broken encoding for extended notation */
      FILE *log = fopen("/tmp/logs/4359.txt", "a");
      if (log != NULL)
      {
        fprintf(log, "MHD_digest_auth_get_username: uname_type mismatch (expected=%d, got=%d) -> freeing username and returning NULL\n",
                (int) uname_type, (int) uname_strct.uname_type);
        fclose(log);
      }

      free (username);
      return NULL;
    }
    (void) used; /* Mute compiler warning for non-debug builds */
    mhd_assert (buf_size >= used);

    {
      FILE *log = fopen("/tmp/logs/4359.txt", "a");
      if (log != NULL)
      {
        fprintf(log, "MHD_digest_auth_get_username: successfully parsed username (buf_size=%zu, used=%zu)\n",
                buf_size, used);
        fclose(log);
      }
    }
  }

  {
    FILE *log = fopen("/tmp/logs/4359.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "MHD_digest_auth_get_username: returning username=%p\n", (void *) username);
      fclose(log);
    }
  }

  return username;
}
