_MHD_EXTERN char *
MHD_digest_auth_get_username (struct MHD_Connection *connection)
{
  const struct MHD_RqDAuth *params;
  char *username;
  size_t buf_size;
  enum MHD_DigestAuthUsernameType uname_type;
  FILE *log;

  /* Open append-only log for evidence collection. */
  log = fopen("/tmp/logs/4358.txt", "a");
  if (log)
    fprintf(log, "MHD_digest_auth_get_username: entered, connection=%p\n", (void *) connection);

  params = MHD_get_rq_dauth_params_ (connection);
  if (log)
    fprintf(log, "MHD_digest_auth_get_username: params=%p\n", (void *) params);

  if (NULL == params)
  {
    if (log)
      fprintf(log, "MHD_digest_auth_get_username: params is NULL -> returning NULL\n");
    if (log)
      fclose(log);
    return NULL;
  }

  uname_type = get_rq_uname_type (params);
  if (log)
    fprintf(log, "MHD_digest_auth_get_username: uname_type=%d\n", (int) uname_type);

  if ( (MHD_DIGEST_AUTH_UNAME_TYPE_STANDARD != uname_type) &&
       (MHD_DIGEST_AUTH_UNAME_TYPE_EXTENDED != uname_type) )
  {
    if (log)
      fprintf(log, "MHD_digest_auth_get_username: uname_type invalid -> returning NULL\n");
    if (log)
      fclose(log);
    return NULL;
  }

  buf_size = get_rq_unames_size (params, uname_type);
  if (log)
    fprintf(log, "MHD_digest_auth_get_username: buf_size=%zu\n", buf_size);

  mhd_assert (0 != buf_size);

  username = (char *) MHD_calloc_ (1, buf_size);
  if (log)
    fprintf(log, "MHD_digest_auth_get_username: username allocated at %p\n", (void *) username);

  if (NULL == username)
  {
    if (log)
      fprintf(log, "MHD_digest_auth_get_username: allocation failed -> returning NULL\n");
    if (log)
      fclose(log);
    return NULL;
  }

  /* The original code uses a constant conditional if (1). We explicitly log
     that the constant condition was evaluated and the branch taken to provide
     evidence of the executed dead operation. */
  if (1)
  {
    struct MHD_DigestAuthUsernameInfo uname_strct;
    size_t used;

    if (log)
      fprintf(log, "MHD_digest_auth_get_username: evaluated constant condition if (1) -> entering branch\n");

    memset (&uname_strct, 0, sizeof(uname_strct));

    used = get_rq_uname (params, uname_type, &uname_strct,
                         (uint8_t *) username, buf_size);

    if (log)
      fprintf(log, "MHD_digest_auth_get_username: get_rq_uname returned used=%zu, uname_strct.uname_type=%d\n",
              used, (int) uname_strct.uname_type);

    if (uname_type != uname_strct.uname_type)
    { /* Broken encoding for extended notation */
      if (log)
        fprintf(log, "MHD_digest_auth_get_username: uname_type mismatch (expected %d got %d) -> freeing username and returning NULL\n",
                (int) uname_type, (int) uname_strct.uname_type);
      free (username);
      if (log)
        fclose(log);
      return NULL;
    }

    /* The original code contained a no-op cast to quiet compiler warnings:
       (void) used; We perform the no-op cast here as well and log its execution
       to demonstrate that this dead operation was executed. */
    (void) used;
    if (log)
      fprintf(log, "MHD_digest_auth_get_username: executed no-op cast (void) used; (used=%zu)\n", used);

    mhd_assert (buf_size >= used);
    if (log)
      fprintf(log, "MHD_digest_auth_get_username: assertion buf_size >= used (buf_size=%zu, used=%zu)\n", buf_size, used);
  }

  if (log)
    fprintf(log, "MHD_digest_auth_get_username: returning username=%p\n", (void *) username);
  if (log)
    fclose(log);

  return username;
}
