_MHD_EXTERN int
MHD_get_connection_values (struct MHD_Connection *connection,
                           enum MHD_ValueKind kind,
                           MHD_KeyValueIterator iterator,
                           void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;
  FILE *log = NULL;

  /* Open log file for append. This records control flow and state to help
     reproduce the reported Rule 8.13 scenario. */
  log = fopen("/tmp/logs/3845.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER: MHD_get_connection_values connection=%p kind=%d iterator=%p iterator_cls=%p\n",
            (void *)connection, (int)kind, (void *)iterator, iterator_cls);
  }

  if (NULL == connection)
  {
    if (log)
    {
      fprintf(log, "EXIT: connection is NULL -> returning -1\n");
      fclose(log);
    }
    return -1;
  }
  ret = 0;

  /* Walk the linked list of headers and log each step. This preserves the
     original semantics while emitting information on pos and the members
     accessed (kind, header, value) so other agents can observe that 'pos'
     is only used for reads. */
  {
    int idx = 0;
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      if (log)
      {
        fprintf(log, "LOOP idx=%d pos=%p pos->kind=%d header=%p \"%s\" value=%p \"%s\"\n",
                idx,
                (void *)pos,
                (int)pos->kind,
                (void *)pos->header,
                (pos->header != NULL) ? pos->header : "(null)",
                (void *)pos->value,
                (pos->value != NULL) ? pos->value : "(null)");
      }

      if (0 != (pos->kind & kind))
      {
        ret++;
        if (log)
          fprintf(log, "MATCH idx=%d ret=%d\n", idx, ret);

        if ((NULL != iterator) &&
            (MHD_NO == iterator (iterator_cls,
                                 pos->kind,
                                 pos->header,
                                 pos->value)))
        {
          if (log)
          {
            fprintf(log, "ITERATOR requested stop at idx=%d ret=%d -> returning\n", idx, ret);
            fclose(log);
          }
          return ret;
        }
      }
      idx++;
    }
  }

  if (log)
  {
    fprintf(log, "EXIT: returning ret=%d\n", ret);
    fclose(log);
  }
  return ret;
}
