_MHD_EXTERN int
MHD_get_connection_values (struct MHD_Connection *connection,
                           enum MHD_ValueKind kind,
                           MHD_KeyValueIterator iterator,
                           void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;
  FILE *log = fopen("/tmp/logs/3845.txt", "a");

  if (log)
  {
    fprintf(log, "MHD_get_connection_values: entry - connection=%p kind=%lu iterator=%p iterator_cls=%p\n",
            (void *)connection, (unsigned long)kind, (void *)iterator, iterator_cls);
    fflush(log);
  }

  if (NULL == connection)
  {
    if (log)
    {
      fprintf(log, "MHD_get_connection_values: connection is NULL, returning -1\n");
      fclose(log);
    }
    return -1;
  }

  ret = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (log)
    {
      fprintf(log, "iterating header node: pos=%p kind=%lu header=%p \"%s\" value=%p \"%s\" next=%p\n",
              (void *)pos,
              (unsigned long)pos->kind,
              (void *)pos->header,
              pos->header ? pos->header : "(null)",
              (void *)pos->value,
              pos->value ? pos->value : "(null)",
              (void *)pos->next);
      fflush(log);
    }

    if (0 != (pos->kind & kind))
    {
      ret++;
      if (log)
      {
        fprintf(log, "header matched kind mask: incremented ret to %d\n", ret);
        fflush(log);
      }

      if ( (NULL != iterator) &&
           (MHD_NO == iterator (iterator_cls,
                                pos->kind,
                                pos->header,
                                pos->value)) )
      {
        if (log)
        {
          fprintf(log, "iterator requested stop, returning %d\n", ret);
          fclose(log);
        }
        return ret;
      }
    }
  }

  if (log)
  {
    fprintf(log, "MHD_get_connection_values: completed loop, returning %d\n", ret);
    fclose(log);
  }
  return ret;
}
