_MHD_EXTERN int
MHD_get_connection_values (struct MHD_Connection *connection,
                           enum MHD_ValueKind kind,
                           MHD_KeyValueIterator iterator,
                           void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;
  FILE *log = fopen("/tmp/logs/3845.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER: MHD_get_connection_values(connection=%p, kind=%u, iterator=%p, iterator_cls=%p)\n",
            (void *)connection, (unsigned)kind, (void *)iterator, iterator_cls);
  }

  if (NULL == connection)
  {
    if (log)
    {
      fprintf(log, "EXIT: connection is NULL -> return -1\n");
      fclose(log);
    }
    return -1;
  }

  ret = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (log)
    {
      fprintf(log, "LOOP: pos=%p pos->kind=0x%08x header=%s value=%s\n",
              (void *)pos,
              (unsigned)pos->kind,
              (pos->header != NULL) ? pos->header : "(null)",
              (pos->value != NULL) ? pos->value : "(null)");
    }

    if (0 != (pos->kind & kind))
    {
      ret++;
      if (log)
        fprintf(log, "MATCH: kind matched -> incremented ret=%d\n", ret);

      if ( (NULL != iterator) &&
           (MHD_NO == iterator (iterator_cls,
                                pos->kind,
                                pos->header,
                                pos->value)) )
      {
        if (log)
        {
          fprintf(log, "ITERATOR: returned MHD_NO -> returning ret=%d\n", ret);
          fclose(log);
        }
        return ret;
      }
    }
    else
    {
      if (log)
        fprintf(log, "NO_MATCH: pos->kind & kind == 0 -> continue\n");
    }
  }

  if (log)
  {
    fprintf(log, "EXIT: completed loop -> returning ret=%d\n", ret);
    fclose(log);
  }
  return ret;
}
