_MHD_EXTERN int
MHD_get_connection_values_n (struct MHD_Connection *connection,
                             enum MHD_ValueKind kind,
                             MHD_KeyValueIteratorN iterator,
                             void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;

  /* Logging: try to append to the diagnostics file. If logging fails, continue
     silently so behavior is unchanged. */
  {
    FILE *log = NULL;

    /* Open in append mode so multiple runs accumulate evidence. */
    log = fopen("/tmp/logs/4335.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "MHD_get_connection_values_n: ENTER connection=%p kind=%u iterator=%p iterator_cls=%p\n",
              (void *)connection, (unsigned)kind, (void *)iterator, (void *)iterator_cls);
      fclose(log);
    }
  }

  if (NULL == connection)
  {
    /* Log null-connection early-return. */
    FILE *log2 = fopen("/tmp/logs/4335.txt", "a");
    if (NULL != log2)
    {
      fprintf(log2, "MHD_get_connection_values_n: connection is NULL -> returning -1\n");
      fclose(log2);
    }
    return -1;
  }
  ret = 0;

  if (NULL == iterator)
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      /* Log traversal and decision for counting. */
      FILE *log3 = fopen("/tmp/logs/4335.txt", "a");
      if (NULL != log3)
      {
        fprintf(log3, "MHD_get_connection_values_n: visiting header pos=%p kind=%u header=%p header_size=%lu value=%p value_size=%lu\n",
                (void *)pos, (unsigned)pos->kind,
                (void *)pos->header, (unsigned long)pos->header_size,
                (void *)pos->value, (unsigned long)pos->value_size);
        fclose(log3);
      }

      if (0 != (kind & pos->kind))
        ret++;
    }
  else
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
      if (0 != (kind & pos->kind))
      {
        ret++;

        /* Log before invoking the iterator. */
        FILE *log4 = fopen("/tmp/logs/4335.txt", "a");
        if (NULL != log4)
        {
          fprintf(log4, "MHD_get_connection_values_n: invoking iterator for pos=%p kind=%u header=%p header_size=%lu value=%p value_size=%lu\n",
                  (void *)pos, (unsigned)pos->kind,
                  (void *)pos->header, (unsigned long)pos->header_size,
                  (void *)pos->value, (unsigned long)pos->value_size);
          fclose(log4);
        }

        if (MHD_NO == iterator (iterator_cls,
                                pos->kind,
                                pos->header,
                                pos->header_size,
                                pos->value,
                                pos->value_size))
        {
          /* Log early termination requested by iterator. */
          FILE *log5 = fopen("/tmp/logs/4335.txt", "a");
          if (NULL != log5)
          {
            fprintf(log5, "MHD_get_connection_values_n: iterator returned MHD_NO -> returning ret=%d\n", ret);
            fclose(log5);
          }
          return ret;
        }
      }

  /* Log normal return. */
  {
    FILE *log6 = fopen("/tmp/logs/4335.txt", "a");
    if (NULL != log6)
    {
      fprintf(log6, "MHD_get_connection_values_n: EXIT returning ret=%d\n", ret);
      fclose(log6);
    }
  }

  return ret;
}
