_MHD_EXTERN int
MHD_get_connection_values_n (struct MHD_Connection *connection,
                             enum MHD_ValueKind kind,
                             MHD_KeyValueIteratorN iterator,
                             void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;
  FILE *fp = fopen("/tmp/logs/4335.txt", "a");

  if (fp)
  {
    fprintf(fp, "MHD_get_connection_values_n ENTER connection=%p kind=0x%X iterator=%p\n",
            (void *)connection, (unsigned)kind, (void *)iterator);
    fflush(fp);
  }

  if (NULL == connection)
  {
    if (fp)
    {
      fprintf(fp, "connection is NULL -> return -1\n");
      fclose(fp);
    }
    return -1;
  }
  ret = 0;

  if (NULL == iterator)
  {
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      if (fp)
        fprintf(fp, "check pos=%p pos->kind=0x%X header=%p header_size=%llu value=%p value_size=%llu\n",
                (void *)pos,
                (unsigned)pos->kind,
                (void *)pos->header,
                (unsigned long long)pos->header_size,
                (void *)pos->value,
                (unsigned long long)pos->value_size);

      if (0 != (kind & pos->kind))
      {
        ret++;
        if (fp)
          fprintf(fp, "match (no iterator): pos=%p ret=%d\n", (void *)pos, ret);
      }
    }
  }
  else
  {
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
      if (0 != (kind & pos->kind))
      {
        if (fp)
        {
          fprintf(fp, "iterator about to be called: pos=%p pos->kind=0x%X header=%p header_size=%llu value=%p value_size=%llu\n",
                  (void *)pos,
                  (unsigned)pos->kind,
                  (void *)pos->header,
                  (unsigned long long)pos->header_size,
                  (void *)pos->value,
                  (unsigned long long)pos->value_size);
          fflush(fp);
        }
        ret++;
        if (MHD_NO == iterator (iterator_cls,
                                pos->kind,
                                pos->header,
                                pos->header_size,
                                pos->value,
                                pos->value_size))
        {
          if (fp)
          {
            fprintf(fp, "iterator returned MHD_NO -> return %d\n", ret);
            fclose(fp);
          }
          return ret;
        }
      }
  }

  if (fp)
  {
    fprintf(fp, "MHD_get_connection_values_n EXIT ret=%d\n", ret);
    fclose(fp);
  }
  return ret;
}
