_MHD_EXTERN const union MHD_DaemonInfo *
MHD_get_daemon_info (struct MHD_Daemon *daemon,
                     enum MHD_DaemonInfoType info_type,
                     ...)
{
  /* Minimal logging to capture control flow and state for Rule 2.2 analysis. */
  FILE *log = fopen("/tmp/logs/1328.txt", "a");
  if (log)
  {
    fprintf(log, "MHD_get_daemon_info: entered; info_type=%d\n", (int)info_type);
  }

  if (NULL == daemon)
  {
    if (log)
    {
      fprintf(log, "MHD_get_daemon_info: daemon is NULL -> returning NULL\n");
      fclose(log);
    }
    return NULL;
  }

  /* Evaluate/assert expressions in the same manner as the original code,
     but capture intermediate values to the log so we can see whether the
     assertions perform only read checks (i.e., potential dead-code scenario). */
  if (log)
  {
    fprintf(log, "MHD_get_daemon_info: daemon ptr=%p, options=0x%08x, worker_pool=%p, worker_pool_size=%u, listen_fd=%d, connections=%u, port=%u\n",
            (void *)daemon,
            (unsigned)daemon->options,
            (void *)daemon->worker_pool,
            (unsigned)daemon->worker_pool_size,
            (int)daemon->listen_fd,
            (unsigned)daemon->connections,
            (unsigned)daemon->port);
  }

  /* First assert: evaluate subexpressions and log them. */
  {
    int opt_select_1 = (0 == (daemon->options & MHD_USE_SELECT_INTERNALLY));
    int worker_nonnull_1 = (NULL != daemon->worker_pool);
    int tid_valid_1 = MHD_thread_handle_ID_is_valid_handle_ (daemon->tid);

    if (log)
    {
      fprintf(log, "assert1: opt_select=%d, worker_nonnull=%d, tid_valid=%d\n",
              opt_select_1, worker_nonnull_1, tid_valid_1);
    }

    mhd_assert ((opt_select_1) || (worker_nonnull_1) || (tid_valid_1));
  }

  /* Second assert: evaluate subexpressions and log them. */
  {
    int left_and_2 = ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) &&
                      (NULL == daemon->worker_pool));
    int tid_valid_2 = MHD_thread_handle_ID_is_valid_handle_ (daemon->tid);

    if (log)
    {
      fprintf(log, "assert2: left_and=%d, tid_valid=%d\n",
              left_and_2, tid_valid_2);
    }

    mhd_assert ((left_and_2) || (! tid_valid_2));
  }

  switch (info_type)
  {
  case MHD_DAEMON_INFO_KEY_SIZE:
    if (log)
    {
      fprintf(log, "case: MHD_DAEMON_INFO_KEY_SIZE -> return NULL\n");
      fclose(log);
    }
    return NULL;   /* no longer supported */
  case MHD_DAEMON_INFO_MAC_KEY_SIZE:
    if (log)
    {
      fprintf(log, "case: MHD_DAEMON_INFO_MAC_KEY_SIZE -> return NULL\n");
      fclose(log);
    }
    return NULL;   /* no longer supported */
  case MHD_DAEMON_INFO_LISTEN_FD:
    daemon->daemon_info_dummy_listen_fd.listen_fd = daemon->listen_fd;
    if (log)
    {
      fprintf(log, "case: MHD_DAEMON_INFO_LISTEN_FD -> listen_fd=%d\n", (int)daemon->listen_fd);
      fclose(log);
    }
    return &daemon->daemon_info_dummy_listen_fd;
  case MHD_DAEMON_INFO_EPOLL_FD:
#ifdef EPOLL_SUPPORT
    daemon->daemon_info_dummy_epoll_fd.epoll_fd = daemon->epoll_fd;
    if (log)
    {
      fprintf(log, "case: MHD_DAEMON_INFO_EPOLL_FD -> epoll_fd=%d\n", (int)daemon->epoll_fd);
      fclose(log);
    }
    return &daemon->daemon_info_dummy_epoll_fd;
#else  /* ! EPOLL_SUPPORT */
    if (log)
    {
      fprintf(log, "case: MHD_DAEMON_INFO_EPOLL_FD -> EPOLL not supported -> return NULL\n");
      fclose(log);
    }
    return NULL;
#endif /* ! EPOLL_SUPPORT */
  case MHD_DAEMON_INFO_CURRENT_CONNECTIONS:
    if (! MHD_D_IS_THREAD_SAFE_ (daemon))
    {
      if (log)
        fprintf(log, "case: CURRENT_CONNECTIONS -> not thread safe -> calling MHD_cleanup_connections\n");
      MHD_cleanup_connections (daemon);
    }
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    else if (daemon->worker_pool)
    {
      unsigned int i;
      /* Collect the connection information stored in the workers. */
      daemon->connections = 0;
      if (log)
        fprintf(log, "case: CURRENT_CONNECTIONS -> thread safe with worker_pool -> size=%u\n", daemon->worker_pool_size);
      for (i = 0; i < daemon->worker_pool_size; i++)
      {
        /* FIXME: next line is thread-safe only if read is atomic. */
        if (log)
          fprintf(log, "  reading worker_pool[%u].connections = %u\n", i, (unsigned)daemon->worker_pool[i].connections);
        daemon->connections += daemon->worker_pool[i].connections;
      }
    }
#endif
    daemon->daemon_info_dummy_num_connections.num_connections
      = daemon->connections;
    if (log)
    {
      fprintf(log, "case: CURRENT_CONNECTIONS -> total connections=%u\n", (unsigned)daemon->connections);
      fclose(log);
    }
    return &daemon->daemon_info_dummy_num_connections;
  case MHD_DAEMON_INFO_FLAGS:
    daemon->daemon_info_dummy_flags.flags = daemon->options;
    if (log)
    {
      fprintf(log, "case: FLAGS -> options=0x%08x\n", (unsigned)daemon->options);
      fclose(log);
    }
    return &daemon->daemon_info_dummy_flags;
  case MHD_DAEMON_INFO_BIND_PORT:
    daemon->daemon_info_dummy_port.port = daemon->port;
    if (log)
    {
      fprintf(log, "case: BIND_PORT -> port=%u\n", (unsigned)daemon->port);
      fclose(log);
    }
    return &daemon->daemon_info_dummy_port;
  default:
    if (log)
    {
      fprintf(log, "case: default -> return NULL\n");
      fclose(log);
    }
    return NULL;
  }
}
