_MHD_EXTERN enum MHD_Result
MHD_get_fdset2 (struct MHD_Daemon *daemon,
                fd_set *read_fd_set,
                fd_set *write_fd_set,
                fd_set *except_fd_set,
                MHD_socket *max_fd,
                unsigned int fd_setsize)
{
  /* Probe: log entry and incoming parameter state */
  {
    FILE *__mhd_log_f = fopen("/tmp/logs/3232.txt", "a");
    if (__mhd_log_f != NULL)
    {
      fprintf(__mhd_log_f, "ENTER MHD_get_fdset2: daemon=%p read_fd_set=%p write_fd_set=%p except_fd_set=%p max_fd=%p fd_setsize=%u\n",
              (void *)daemon, (void *)read_fd_set, (void *)write_fd_set, (void *)except_fd_set, (void *)max_fd, fd_setsize);
      fclose(__mhd_log_f);
    }
  }

  if ( (NULL == daemon) ||
       (NULL == read_fd_set) ||
       (NULL == write_fd_set) ||
       MHD_D_IS_USING_THREADS_ (daemon) ||
       MHD_D_IS_USING_POLL_ (daemon))
    return MHD_NO;

#ifdef HAVE_MESSAGES
  if (NULL == except_fd_set)
  {
    MHD_DLOG (daemon,
              _ ("MHD_get_fdset2() called with except_fd_set "
                 "set to NULL. Such behavior is unsupported.\n"));
  }
#endif

#ifdef HAS_FD_SETSIZE_OVERRIDABLE
  if (0 == fd_setsize)
    return MHD_NO;
  else if (((unsigned int) INT_MAX) < fd_setsize)
  {
    /* Log the adjustment that modifies the parameter fd_setsize */
    {
      FILE *__mhd_log_f = fopen("/tmp/logs/3232.txt", "a");
      if (__mhd_log_f != NULL)
      {
        fprintf(__mhd_log_f, "ADJUST: fd_setsize (%u) > INT_MAX, will set to %d\n", fd_setsize, INT_MAX);
        fclose(__mhd_log_f);
      }
    }
    fd_setsize = (unsigned int) INT_MAX;
    /* Log after modification */
    {
      FILE *__mhd_log_f = fopen("/tmp/logs/3232.txt", "a");
      if (__mhd_log_f != NULL)
      {
        fprintf(__mhd_log_f, "AFTER ADJUST: fd_setsize=%u\n", fd_setsize);
        fclose(__mhd_log_f);
      }
    }
  }
#ifdef HAVE_MESSAGES
  else if (daemon->fdset_size > ((int) fd_setsize))
  {
    if (daemon->fdset_size_set_by_app)
    {
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n"),
                "MHD_get_fdset2", fd_setsize, daemon->fdset_size);
    }
    else
    {
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than FD_SETSIZE used by MHD (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n"),
                "MHD_get_fdset2", fd_setsize, daemon->fdset_size);
    }
  }
#endif /* HAVE_MESSAGES */
#else  /* ! HAS_FD_SETSIZE_OVERRIDABLE */
  if (((unsigned int) FD_SETSIZE) > fd_setsize)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("%s() called with fd_setsize (%u) " \
                 "less than fixed FD_SETSIZE value (%d) used on the " \
                 "platform.\n"),
              "MHD_get_fdset2", fd_setsize, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
    return MHD_NO;
  }
  /* Help compiler to optimise */
  /* Log the violating assignment to the non-pointer function parameter */
  {
    FILE *__mhd_log_f = fopen("/tmp/logs/3232.txt", "a");
    if (__mhd_log_f != NULL)
    {
      fprintf(__mhd_log_f, "VIOLATION_SCENARIO: About to assign fd_setsize = (int)FD_SETSIZE; original fd_setsize=%u FD_SETSIZE=%d\n", fd_setsize, (int) FD_SETSIZE);
      fclose(__mhd_log_f);
    }
  }
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
  /* Log after the assignment to provide evidence */
  {
    FILE *__mhd_log_f = fopen("/tmp/logs/3232.txt", "a");
    if (__mhd_log_f != NULL)
    {
      fprintf(__mhd_log_f, "VIOLATION_SCENARIO: After assignment fd_setsize=%u\n", fd_setsize);
      fclose(__mhd_log_f);
    }
  }
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

#ifdef EPOLL_SUPPORT
  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
    if (daemon->shutdown)
      return MHD_YES;

    /* we're in epoll mode, use the epoll FD as a stand-in for
       the entire event set */

    return MHD_add_to_fd_set_ (daemon->epoll_fd,
                               read_fd_set,
                               max_fd,
                               (int) fd_setsize) ? MHD_YES : MHD_NO;
  }
#endif

  return internal_get_fdset2 (daemon,
                              read_fd_set,
                              write_fd_set,
                              except_fd_set,
                              max_fd,
                              (int) fd_setsize);
}
