_MHD_EXTERN enum MHD_Result
MHD_get_fdset2 (struct MHD_Daemon *daemon,
                fd_set *read_fd_set,
                fd_set *write_fd_set,
                fd_set *except_fd_set,
                MHD_socket *max_fd,
                unsigned int fd_setsize)
{
  /* Logging probe: append control flow and state info to the log file. */
  FILE *logf = fopen("/tmp/logs/3235.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER: MHD_get_fdset2 daemon=%p read_fd_set=%p write_fd_set=%p except_fd_set=%p max_fd=%p fd_setsize=%u\n",
            (void *) daemon, (void *) read_fd_set, (void *) write_fd_set,
            (void *) except_fd_set, (void *) max_fd, fd_setsize);
    fflush(logf);
  }

  if ( (NULL == daemon) ||
       (NULL == read_fd_set) ||
       (NULL == write_fd_set) ||
       MHD_D_IS_USING_THREADS_ (daemon) ||
       MHD_D_IS_USING_POLL_ (daemon))
  {
    if (logf)
    {
      fprintf(logf, "EARLY_RETURN: conditions: daemon==%s read_fd_set==%s write_fd_set==%s threads_flag?=%s poll_flag?=%s -> returning MHD_NO\n",
              (daemon == NULL) ? "NULL" : "NON-NULL",
              (read_fd_set == NULL) ? "NULL" : "NON-NULL",
              (write_fd_set == NULL) ? "NULL" : "NON-NULL",
              (MHD_D_IS_USING_THREADS_ (daemon)) ? "TRUE" : "FALSE",
              (MHD_D_IS_USING_POLL_ (daemon)) ? "TRUE" : "FALSE");
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if (NULL == except_fd_set)
  {
    if (logf)
    {
      fprintf(logf, "BRANCH: except_fd_set is NULL -> will call MHD_DLOG for warning\n");
      fflush(logf);
    }

    MHD_DLOG (daemon,
              _ ("MHD_get_fdset2() called with except_fd_set "
                 "set to NULL. Such behavior is unsupported.\n"));
  }
#endif

#ifdef HAS_FD_SETSIZE_OVERRIDABLE
  if (0 == fd_setsize)
  {
    if (logf)
    {
      fprintf(logf, "BRANCH: fd_setsize == 0 -> returning MHD_NO\n");
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;
  }
  else if (((unsigned int) INT_MAX) < fd_setsize)
    fd_setsize = (unsigned int) INT_MAX;
#ifdef HAVE_MESSAGES
  else if (daemon->fdset_size > ((int) fd_setsize))
  {
    if (daemon->fdset_size_set_by_app)
    {
      if (logf)
      {
        fprintf(logf, "BRANCH: fd_setsize (%u) < daemon->fdset_size (%d) and set by app -> logging via MHD_DLOG\n",
                fd_setsize, daemon->fdset_size);
        fflush(logf);
      }
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than value set by MHD_OPTION_APP_FD_SETSIZE (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Use MHD_OPTION_APP_FD_SETSIZE with the correct value.\n"),
                "MHD_get_fdset2", fd_setsize, daemon->fdset_size);
    }
    else
    {
      if (logf)
      {
        fprintf(logf, "BRANCH: fd_setsize (%u) < daemon->fdset_size (%d) default -> logging via MHD_DLOG\n",
                fd_setsize, daemon->fdset_size);
        fflush(logf);
      }
      MHD_DLOG (daemon,
                _ ("%s() called with fd_setsize (%u) " \
                   "less than FD_SETSIZE used by MHD (%d). " \
                   "Some socket FDs may be not processed. " \
                   "Consider using MHD_OPTION_APP_FD_SETSIZE option.\n"),
                "MHD_get_fdset2", fd_setsize, daemon->fdset_size);
    }
  }
#endif /* HAVE_MESSAGES */
#else  /* ! HAS_FD_SETSIZE_OVERRIDABLE */
  if (((unsigned int) FD_SETSIZE) > fd_setsize)
  {
#ifdef HAVE_MESSAGES
    if (logf)
    {
      fprintf(logf, "BRANCH: fd_setsize (%u) < FD_SETSIZE (%d) -> will call MHD_DLOG and return MHD_NO\n",
              fd_setsize, (int) FD_SETSIZE);
      fflush(logf);
    }
    MHD_DLOG (daemon,
              _ ("%s() called with fd_setsize (%u) " \
                 "less than fixed FD_SETSIZE value (%d) used on the " \
                 "platform.\n"),
              "MHD_get_fdset2", fd_setsize, (int) FD_SETSIZE);
#endif /* HAVE_MESSAGES */
    if (logf)
    {
      fprintf(logf, "BRANCH: returning MHD_NO due to fd_setsize < FD_SETSIZE\n");
      fflush(logf);
      fclose(logf);
    }
    return MHD_NO;
  }
  fd_setsize = (int) FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

#ifdef EPOLL_SUPPORT
  if (MHD_D_IS_USING_EPOLL_ (daemon))
  {
    if (daemon->shutdown)
    {
      if (logf)
      {
        fprintf(logf, "BRANCH: epoll mode and daemon->shutdown==TRUE -> returning MHD_YES\n");
        fflush(logf);
        fclose(logf);
      }
      return MHD_YES;
    }

    /* we're in epoll mode, use the epoll FD as a stand-in for
       the entire event set */
    if (logf)
    {
      fprintf(logf, "BRANCH: epoll mode -> calling MHD_add_to_fd_set_ with epoll_fd=%d fd_setsize=%d\n",
              daemon->epoll_fd, (int) fd_setsize);
      fflush(logf);
    }

    {
      int added = MHD_add_to_fd_set_ (daemon->epoll_fd,
                                      read_fd_set,
                                      max_fd,
                                      (int) fd_setsize);
      enum MHD_Result res = added ? MHD_YES : MHD_NO;
      if (logf)
      {
        fprintf(logf, "RESULT: epoll branch -> MHD_add_to_fd_set_ returned %d -> returning %d\n",
                added, (int) res);
        fflush(logf);
        fclose(logf);
      }
      return res;
    }
  }
#endif

  if (logf)
  {
    fprintf(logf, "PATH: falling through to internal_get_fdset2 with fd_setsize=%d\n", (int) fd_setsize);
    fflush(logf);
  }

  /* preserve original return behavior but log the result */
  enum MHD_Result internal_res = internal_get_fdset2 (daemon,
                                                      read_fd_set,
                                                      write_fd_set,
                                                      except_fd_set,
                                                      max_fd,
                                                      (int) fd_setsize);
  if (logf)
  {
    fprintf(logf, "EXIT: internal_get_fdset2 returned %d -> returning %d\n", (int) internal_res, (int) internal_res);
    fflush(logf);
    fclose(logf);
  }

  return internal_res;
}
