struct MHD_HTTP_Res_Header *
MHD_get_response_element_n_ (struct MHD_Response *response,
                             enum MHD_ValueKind kind,
                             const char *key,
                             size_t key_len)
{
  struct MHD_HTTP_Res_Header *pos;
  FILE *log = NULL;

  mhd_assert (NULL != key);
  mhd_assert (0 != key[0]);
  mhd_assert (0 != key_len);

  /* Open log file for append; if it fails, proceed without logging. */
  log = fopen("/tmp/logs/3025.txt", "a");
  if (NULL != log)
  {
    fprintf(log, "ENTER: MHD_get_response_element_n_: response=%p, kind=%d, key_len=%zu, key=\'%.*s\'\n",
            (void *)response, (int)kind, key_len, (int)key_len, key);
    if (response)
      fprintf(log, "response->first_header=%p\n", (void *)response->first_header);
    fflush(log);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    /* Log current header node state */
    if (NULL != log)
    {
      fprintf(log, "LOOP: pos=%p, pos->next=%p, header_size=%zu, kind=%d, header=%p\n",
              (void *)pos, (void *)pos->next, pos->header_size, (int)pos->kind, (void *)pos->header);
      /* Print header contents conservatively up to header_size */
      if (pos->header && pos->header_size > 0)
      {
        /* Cast size to int for %.*s; test harness should use reasonable lengths */
        fprintf(log, "LOOP: header-content=\'%.*s\'\n", (int)pos->header_size, pos->header);
      }
      fflush(log);
    }

    /* Preserve original evaluation order: only call the expensive/effectful comparison
       when sizes and kinds match, as in the original code. */
    if ((pos->header_size == key_len) && (kind == pos->kind))
    {
      int equal = MHD_str_equal_caseless_bin_n_ (pos->header, key, pos->header_size);

      if (NULL != log)
      {
        fprintf(log, "CHECK: sizes_match=1, kinds_match=1, MHD_str_equal_caseless_bin_n_ returned=%d\n", equal);
        fflush(log);
      }

      if (equal)
      {
        if (NULL != log)
        {
          fprintf(log, "MATCH: returning pos=%p\n", (void *)pos);
          fclose(log);
        }
        return pos;
      }
      else
      {
        /* continue loop; we've already logged the failed equality */
      }
    }
    else
    {
      if (NULL != log)
      {
        fprintf(log, "CHECK: sizes_match=%d, kinds_match=%d -> skipping equality check\n",
                (int)(pos->header_size == key_len), (int)(kind == pos->kind));
        fflush(log);
      }
    }
  }

  if (NULL != log)
  {
    fprintf(log, "EXIT: MHD_get_response_element_n_: no match found, returning NULL\n");
    fclose(log);
  }
  return NULL;
}
