#include <stdio.h>
#include <string.h>

_MHD_EXTERN const char *
MHD_get_response_header (struct MHD_Response *response,
                         const char *key)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t key_size;

  /* Minimal entry log so that the function reachability is visible. */
  FILE *logf = fopen("/tmp/logs/23.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER: MHD_get_response_header(response=%p, key=%p)\n",
            (void *) response, (void *) key);
    fclose(logf);
  }

  if (NULL == key)
  {
    logf = fopen("/tmp/logs/23.txt", "a");
    if (logf)
    {
      fprintf(logf, "EARLY_RETURN: key is NULL -> return NULL\n");
      fclose(logf);
    }
    return NULL;
  }

  /* This is the original potentially-unsafe call that can trigger the
     Rule 21.17 violation if `key` does not point to a NUL-terminated
     character sequence. We keep the call to preserve original logic and
     to allow reproducing the scenario. */
  logf = fopen("/tmp/logs/23.txt", "a");
  if (logf)
  {
    fprintf(logf, "BEFORE_STRLEN: about to call strlen(key) on pointer %p\n",
            (void *) key);
    fclose(logf);
  }

  key_size = strlen (key);

  logf = fopen("/tmp/logs/23.txt", "a");
  if (logf)
  {
    fprintf(logf, "AFTER_STRLEN: key_size = %zu\n", key_size);
    fclose(logf);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    /* Log current header node information (pointers and sizes only, do not
       attempt to print header bytes as a C string since that could read
       out-of-bounds). */
    logf = fopen("/tmp/logs/23.txt", "a");
    if (logf)
    {
      fprintf(logf,
              "LOOP: pos=%p, pos->header=%p, pos->header_size=%zu, pos->value=%p\n",
              (void *) pos, (void *) pos->header, pos->header_size,
              (void *) pos->value);
      fclose(logf);
    }

    if ((pos->header_size == key_size) &&
        (MHD_str_equal_caseless_bin_n_ (pos->header, key, pos->header_size)))
    {
      logf = fopen("/tmp/logs/23.txt", "a");
      if (logf)
      {
        fprintf(logf, "MATCH_FOUND: returning pos->value=%p\n",
                (void *) pos->value);
        fclose(logf);
      }
      return pos->value;
    }
  }

  logf = fopen("/tmp/logs/23.txt", "a");
  if (logf)
  {
    fprintf(logf, "NOT_FOUND: no matching header -> return NULL\n");
    fclose(logf);
  }

  return NULL;
}
