_MHD_EXTERN const char *
MHD_get_response_header (struct MHD_Response *response,
                         const char *key)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t key_size;
  FILE *log = fopen("/tmp/logs/25.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER MHD_get_response_header: response=%p, key=%p\n",
            (void *)response, (const void *)key);
    fflush(log);
  }

  if (NULL == key)
  {
    if (log)
    {
      fprintf(log, "KEY IS NULL -> returning NULL\n");
      fclose(log);
    }
    return NULL;
  }

  key_size = strlen (key);
  if (log)
  {
    /* key is expected to be a NUL-terminated string provided by caller */
    fprintf(log, "key_size=%zu, key=\"%s\"\n", key_size, key);
  }

  {
    size_t idx = 0u;
    for (pos = response->first_header;
         NULL != pos;
         pos = pos->next)
    {
      if (log)
      {
        fprintf(log, "header[%zu]: pos=%p, header=%p, header_size=%zu, value=%p\n",
                idx, (void *)pos,
                (void *)pos->header,
                (size_t)pos->header_size,
                (void *)pos->value);
        fprintf(log, "header content (up to 256 bytes): ");
        if (pos->header != NULL && pos->header_size > 0)
        {
          size_t to_write = pos->header_size;
          if (to_write > 256u)
            to_write = 256u;
          /* write raw bytes of header (may not be NUL-terminated) */
          fwrite(pos->header, 1, to_write, log);
          if (pos->header_size > to_write)
            fprintf(log, "...");
        }
        else
        {
          fprintf(log, "(NULL or empty)");
        }
        fprintf(log, "\n");
        fflush(log);
      }

      if ((pos->header_size == key_size) &&
          (MHD_str_equal_caseless_bin_n_ (pos->header, key, pos->header_size)))
      {
        if (log)
        {
          fprintf(log, "MATCH at header[%zu]: pos=%p, returning value=%p\n",
                  idx, (void *)pos, (void *)pos->value);
          if (pos->value != NULL)
            fprintf(log, "value=\"%s\"\n", pos->value);
          else
            fprintf(log, "value=NULL\n");
          fclose(log);
        }
        return pos->value;
      }

      idx++;
    }
  }

  if (log)
  {
    fprintf(log, "no matching header found -> returning NULL\n");
    fclose(log);
  }
  return NULL;
}
