_MHD_EXTERN const char *
MHD_get_response_header (struct MHD_Response *response,
                         const char *key)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t key_size;
  FILE *log = fopen("/tmp/logs/25.txt", "a");

  if (log)
  {
    fprintf(log, "MHD_get_response_header ENTER: response=%p, key=%p\n",
            (void *)response, (void *)key);
  }

  if (NULL == key)
  {
    if (log)
    {
      fprintf(log, "  key is NULL -> returning NULL\n");
      fclose(log);
    }
    return NULL;
  }

  key_size = strlen (key);
  if (log)
  {
    fprintf(log, "  key='%s', key_size=%zu\n", key, key_size);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    if (log)
    {
      fprintf(log,
              "  iter header pos=%p header_ptr=%p header_size=%zu value_ptr=%p\n",
              (void *)pos, (void *)pos->header, pos->header_size,
              (void *)pos->value);
    }

    if ((pos->header_size == key_size) &&
        (MHD_str_equal_caseless_bin_n_ (pos->header, key, pos->header_size)))
    {
      if (log)
      {
        if (pos->value)
          fprintf(log, "  MATCH at pos=%p -> value_ptr=%p value='%s'\n",
                  (void *)pos, (void *)pos->value, pos->value);
        else
          fprintf(log, "  MATCH at pos=%p -> value_ptr=NULL\n", (void *)pos);
        fclose(log);
      }
      return pos->value;
    }
    else
    {
      if (log)
        fprintf(log, "  no match at pos=%p\n", (void *)pos);
    }
  }

  if (log)
  {
    fprintf(log, "  no header matched -> returning NULL\n");
    fclose(log);
  }
  return NULL;
}
