_MHD_EXTERN const char *
MHD_get_response_header (struct MHD_Response *response,
                         const char *key)
{
  struct MHD_HTTP_Res_Header *pos;
  size_t key_size;
  FILE *log = fopen("/tmp/logs/25.txt", "a");

  if (log)
  {
    fprintf(log, "ENTRY: MHD_get_response_header response=%p key=%s\n",
            (void *)response, (key != NULL) ? key : "(null)");
    fflush(log);
  }

  if (NULL == key)
  {
    if (log)
    {
      fprintf(log, "EXIT: key is NULL -> return NULL\n");
      fclose(log);
    }
    return NULL;
  }

  key_size = strlen (key);
  if (log)
  {
    fprintf(log, "key_size=%zu\n", key_size);
    fflush(log);
  }

  unsigned int iter = 0u;
  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    ++iter;
    if (log)
    {
      fprintf(log,
              "ITER %u: pos=%p header_size=%zu header=%p value=%p next=%p\n",
              iter,
              (void *)pos,
              pos->header_size,
              (void *)pos->header,
              (void *)pos->value,
              (void *)pos->next);
      fflush(log);
    }

    if ((pos->header_size == key_size) &&
        (MHD_str_equal_caseless_bin_n_ (pos->header, key, pos->header_size)))
    {
      if (log)
      {
        fprintf(log, "MATCH at ITER %u: returning value=%p\n", iter, (void *)pos->value);
        fclose(log);
      }
      return pos->value;
    }
  }

  if (log)
  {
    fprintf(log, "EXIT: no match found -> return NULL\n");
    fclose(log);
  }
  return NULL;
}
