_MHD_EXTERN int
MHD_get_response_headers (struct MHD_Response *response,
                          MHD_KeyValueIterator iterator,
                          void *iterator_cls)
{
  int numHeaders = 0;
  struct MHD_HTTP_Res_Header *pos;
  FILE *log = fopen("/tmp/logs/2925.txt", "a");

  if (log)
  {
    fprintf(log, "Entered MHD_get_response_headers: response=%p iterator=%p iterator_cls=%p\n",
            (void *) response, (void *) iterator, iterator_cls);
    fflush(log);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    numHeaders++;

    if (log)
    {
      fprintf(log,
              "Loop #%d: pos=%p kind=%d header=%p value=%p header_str=%s value_str=%s\n",
              numHeaders,
              (void *) pos,
              (int) pos->kind,
              (void *) pos->header,
              (void *) pos->value,
              (pos->header != NULL) ? pos->header : "(null)",
              (pos->value != NULL) ? pos->value : "(null)");
      fflush(log);
    }

    if ((NULL != iterator) &&
        (MHD_NO == iterator (iterator_cls,
                             pos->kind,
                             pos->header,
                             pos->value)))
    {
      if (log)
      {
        fprintf(log, "Iterator returned MHD_NO at loop #%d, pos=%p. Breaking.\n",
                numHeaders, (void *) pos);
        fflush(log);
      }
      break;
    }
  }

  if (log)
  {
    fprintf(log, "Exiting MHD_get_response_headers: numHeaders=%d\n", numHeaders);
    fclose(log);
  }

  return numHeaders;
}
