_MHD_EXTERN int
MHD_get_response_headers (struct MHD_Response *response,
                          MHD_KeyValueIterator iterator,
                          void *iterator_cls)
{
  int numHeaders = 0;
  struct MHD_HTTP_Res_Header *pos;
  FILE *logf = fopen("/tmp/logs/2925.txt", "a");

  if (logf != NULL)
  {
    /* Log function entry and pointers; avoid dereferencing response if NULL. */
    if (response != NULL)
      fprintf(logf, "ENTRY: MHD_get_response_headers response=%p, first_header=%p\n",
              (void *)response, (void *)response->first_header);
    else
      fprintf(logf, "ENTRY: MHD_get_response_headers response=NULL\n");
    fflush(logf);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    numHeaders++;

    if (logf != NULL)
    {
      /* Log current header node state. Do not assume header/value are non-NULL. */
      fprintf(logf, "LOOP: index=%d, node=%p, kind=%d, header_ptr=%p, value_ptr=%p\n",
              numHeaders, (void *)pos, (int)pos->kind,
              (void *)pos->header, (void *)pos->value);

      if (pos->header != NULL)
        fprintf(logf, "LOOP: header_str=\"%s\"\n", pos->header);
      else
        fprintf(logf, "LOOP: header_str=(null)\n");

      if (pos->value != NULL)
        fprintf(logf, "LOOP: value_str=\"%s\"\n", pos->value);
      else
        fprintf(logf, "LOOP: value_str=(null)\n");

      fflush(logf);
    }

    if ((NULL != iterator) &&
        (MHD_NO == iterator (iterator_cls,
                             pos->kind,
                             pos->header,
                             pos->value)))
    {
      if (logf != NULL)
      {
        fprintf(logf, "ITERATOR returned MHD_NO, breaking after index=%d\n", numHeaders);
        fflush(logf);
      }
      break;
    }
  }

  if (logf != NULL)
  {
    fprintf(logf, "EXIT: MHD_get_response_headers numHeaders=%d\n", numHeaders);
    fclose(logf);
  }

  return numHeaders;
}
