_MHD_EXTERN int
MHD_get_response_headers (struct MHD_Response *response,
                          MHD_KeyValueIterator iterator,
                          void *iterator_cls)
{
  int numHeaders = 0;
  struct MHD_HTTP_Res_Header *pos;
  FILE *logf = fopen("/tmp/logs/2925.txt", "a");

  if (logf)
  {
    fprintf(logf, "MHD_get_response_headers: entered\n");
    fprintf(logf, "  response=%p, iterator=%p, iterator_cls=%p\n",
            (void *) response, (void *) iterator, iterator_cls);
    /* Avoid dereferencing response here to keep behavior same as original
       (the original code will dereference response->first_header below). */
    if (response)
      fprintf(logf, "  response->first_header=%p\n", (void *) response->first_header);
    fflush(logf);
  }

  for (pos = response->first_header;
       NULL != pos;
       pos = pos->next)
  {
    numHeaders++;

    if (logf)
    {
      fprintf(logf, "  iter %d: pos=%p, next=%p, kind=%d, header=%s, value=%s\n",
              numHeaders - 1,
              (void *) pos,
              (void *) pos->next,
              (int) pos->kind,
              pos->header ? pos->header : "(null)",
              pos->value ? pos->value : "(null)");
      fflush(logf);
    }

    if ((NULL != iterator) &&
        (MHD_NO == iterator (iterator_cls,
                             pos->kind,
                             pos->header,
                             pos->value)))
    {
      if (logf)
      {
        fprintf(logf, "  iterator returned MHD_NO; breaking at iter %d\n", numHeaders - 1);
        fflush(logf);
      }
      break;
    }
  }

  if (logf)
  {
    fprintf(logf, "MHD_get_response_headers: exiting, numHeaders=%d\n", numHeaders);
    fclose(logf);
  }

  return numHeaders;
}
