size_t
MHD_hex_to_bin (const char *hex,
                size_t len,
                void *bin)
{
  uint8_t *const out = (uint8_t *) bin;
  size_t r;
  size_t w;
  FILE *log = fopen("/tmp/logs/4048.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_hex_to_bin: hex=%p len=%zu bin=%p\n", (const void *) hex, len, bin);
    if (len > 0 && hex != NULL)
    {
      unsigned int c0 = (unsigned char) hex[0];
      unsigned int c1 = (len > 1) ? (unsigned char) hex[1] : 0;
      fprintf(log, "hex[0]=0x%02x (%c) hex[1]=0x%02x (%c)\n",
              c0, (c0 >= 32 && c0 < 127) ? hex[0] : '.',
              c1, (c1 >= 32 && c1 < 127) ? hex[1] : '.');
    }
  }

  if (0 == len)
  {
    if (log)
    {
      fprintf(log, "EARLY RETURN: len == 0 -> return 0\n");
      fclose(log);
    }
    return 0;
  }
  r = 0;
  w = 0;
  if (0 != len % 2)
  {
    /* Assume the first byte is encoded with single digit */
    const int l = toxdigitvalue (hex[r++]);
    if (0 > l)
    {
      if (log)
      {
        fprintf(log, "INVALID_DIGIT: first digit invalid (l=%d) at r=%zu -> return 0\n", l, r - 1);
        fclose(log);
      }
      return 0;
    }
    out[w++] = (uint8_t) ((unsigned int) l);
    if (log)
      fprintf(log, "PROCESSED_SINGLE_DIGIT: l=%d -> out[0]=0x%02x w=%zu r=%zu\n", l, out[0], w, r);
  }
  while (r < len)
  {
    const int h = toxdigitvalue (hex[r++]);
    const int l = toxdigitvalue (hex[r++]);
    if ((0 > h) || (0 > l))
    {
      if (log)
      {
        fprintf(log, "INVALID_DIGIT_PAIR: h=%d l=%d at r=%zu -> return 0\n", h, l, r - 2);
        fclose(log);
      }
      return 0;
    }
    out[w++] = (uint8_t) ( ((uint8_t) (((uint8_t) ((unsigned int) h)) << 4))
                           | ((uint8_t) ((unsigned int) l)) );
    if (log)
      fprintf(log, "PROCESSED_PAIR: h=%d l=%d -> out[%zu]=0x%02x r=%zu\n", h, l, w - 1, out[w - 1], r);
  }

  if (log)
  {
    fprintf(log, "ABOUT_TO_ASSERT: len==%zu r==%zu (will call mhd_assert(len == r))\n", len, r);
    fflush(log);
  }
  mhd_assert (len == r);
  if (log)
  {
    fprintf(log, "AFTER_ASSERT: mhd_assert(len == r) returned (no abort)\n");
    fprintf(log, "ABOUT_TO_ASSERT: (len + 1) / 2 == %zu w == %zu (will call mhd_assert((len + 1) / 2 == w))\n", (len + 1) / 2, w);
    fflush(log);
  }
  mhd_assert ((len + 1) / 2 == w);
  if (log)
  {
    fprintf(log, "AFTER_ASSERT: mhd_assert((len + 1) / 2 == w) returned (no abort)\n");
    fprintf(log, "EXIT MHD_hex_to_bin: returning w=%zu\n", w);
    fclose(log);
  }
  return w;
}
