void
MHD_init (void)
{
#if defined(MHD_WINSOCK_SOCKETS)
  WSADATA wsd;
#endif /* MHD_WINSOCK_SOCKETS */

  /* Open a persistent log entry for this function. Keep the file open
     for the duration of the function to capture control flow and state.
     Use append mode so multiple runs accumulate evidence. */
  {
    FILE *mhd_log = fopen("/tmp/logs/2679.txt", "a");

    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: entered\n");
      fflush(mhd_log);
    }

    MHD_set_panic_func (NULL, NULL);

#if defined(MHD_WINSOCK_SOCKETS)
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: MHD_WINSOCK_SOCKETS enabled\n");
      fflush(mhd_log);
    }
    if (0 != WSAStartup (MAKEWORD (2, 2), &wsd))
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: WSAStartup failed (return != 0)\n");
        fflush(mhd_log);
      }
      MHD_PANIC (_ ("Failed to initialize winsock.\n"));
    }
    if ((2 != LOBYTE (wsd.wVersion)) && (2 != HIBYTE (wsd.wVersion)))
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: winsock version mismatch: wVersion=0x%04x\n", (unsigned)wsd.wVersion);
        fflush(mhd_log);
      }
      MHD_PANIC (_ ("Winsock version 2.2 is not available.\n"));
    }
#endif /* MHD_WINSOCK_SOCKETS */

#ifdef HTTPS_SUPPORT
#ifdef MHD_HTTPS_REQUIRE_GCRYPT
#if GCRYPT_VERSION_NUMBER < 0x010600
#if GNUTLS_VERSION_NUMBER <= 0x020b00
#if defined(MHD_USE_POSIX_THREADS)
    if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                           &gcry_threads_pthread))
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: gcry_control set pthread cbs failed\n");
        fflush(mhd_log);
      }
      MHD_PANIC (_ ("Failed to initialise multithreading in libgcrypt.\n"));
    }
#elif defined(MHD_W32_MUTEX_)
    if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                           &gcry_threads_w32))
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: gcry_control set w32 cbs failed\n");
        fflush(mhd_log);
      }
      MHD_PANIC (_ ("Failed to initialise multithreading in libgcrypt.\n"));
    }
#endif /* defined(MHD_W32_MUTEX_) */
#endif /* GNUTLS_VERSION_NUMBER <= 0x020b00 */
    gcry_check_version (NULL);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: gcry_check_version(NULL) called\n");
      fflush(mhd_log);
    }
#else
    if (NULL == gcry_check_version ("1.6.0"))
    {
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: libgcrypt too old for requirement '1.6.0'\n");
        fflush(mhd_log);
      }
      MHD_PANIC (_ ("libgcrypt is too old. MHD was compiled for " \
                    "libgcrypt 1.6.0 or newer.\n"));
    }
#endif
#endif /* MHD_HTTPS_REQUIRE_GCRYPT */
    gnutls_global_init ();
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: gnutls_global_init() called\n");
      fflush(mhd_log);
    }
#endif /* HTTPS_SUPPORT */

    MHD_monotonic_sec_counter_init ();
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: MHD_monotonic_sec_counter_init() called\n");
      fflush(mhd_log);
    }

    MHD_send_init_static_vars_ ();
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: MHD_send_init_static_vars_() called\n");
      fflush(mhd_log);
    }

    MHD_init_mem_pools_ ();
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: MHD_init_mem_pools_() called\n");
      fflush(mhd_log);
    }

    /* Check whether sizes were correctly detected by configure */
#ifdef _DEBUG
    if (1)
    {
      struct timeval tv;
      if (mhd_log)
      {
        fprintf(mhd_log, "MHD_init: DEBUG block entered (if (1) taken)\n");
        fprintf(mhd_log, "MHD_init: sizeof(tv.tv_sec)=%zu expected=%zu\n",
                sizeof(tv.tv_sec), (size_t)SIZEOF_STRUCT_TIMEVAL_TV_SEC);
        fflush(mhd_log);
      }
      mhd_assert (sizeof(tv.tv_sec) == SIZEOF_STRUCT_TIMEVAL_TV_SEC);
    }
#endif /* _DEBUG */

    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_init: final assertion check for uint64_t size: sizeof(uint64_t)=%zu expected=%zu\n",
              sizeof(uint64_t), (size_t)SIZEOF_UINT64_T);
      fflush(mhd_log);
      fclose(mhd_log);
    }
  }

  mhd_assert (sizeof(uint64_t) == SIZEOF_UINT64_T);
}
