void
MHD_init (void)
{
#if defined(MHD_WINSOCK_SOCKETS)
  WSADATA wsd;
#endif /* MHD_WINSOCK_SOCKETS */

  /* Log entry into MHD_init */
  {
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: entered\n");
      fclose (f);
    }
  }

  MHD_set_panic_func (NULL, NULL);

#if defined(MHD_WINSOCK_SOCKETS)
  if (0 != WSAStartup (MAKEWORD (2, 2), &wsd))
  {
    /* Log the panic message and its pointer before invoking MHD_PANIC */
    const char *mhd_msg = _ ("Failed to initialize winsock.\n");
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: WSAStartup failed - about to call MHD_PANIC\n");
      fprintf (f, "MHD_init: panic_msg_ptr=%p panic_msg=\"%s\"\n",
               (void *)mhd_msg, (mhd_msg != NULL) ? mhd_msg : "(null)");
      fclose (f);
    }
    MHD_PANIC ((char *)mhd_msg);
  }
  if ((2 != LOBYTE (wsd.wVersion)) && (2 != HIBYTE (wsd.wVersion)))
  {
    const char *mhd_msg = _ ("Winsock version 2.2 is not available.\n");
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: Winsock version mismatch - about to call MHD_PANIC\n");
      fprintf (f, "MHD_init: panic_msg_ptr=%p panic_msg=\"%s\"\n",
               (void *)mhd_msg, (mhd_msg != NULL) ? mhd_msg : "(null)");
      fclose (f);
    }
    MHD_PANIC ((char *)mhd_msg);
  }
#endif /* MHD_WINSOCK_SOCKETS */
#ifdef HTTPS_SUPPORT
#ifdef MHD_HTTPS_REQUIRE_GCRYPT
#if GCRYPT_VERSION_NUMBER < 0x010600
#if GNUTLS_VERSION_NUMBER <= 0x020b00
#if defined(MHD_USE_POSIX_THREADS)
  if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                         &gcry_threads_pthread))
  {
    const char *mhd_msg = _ ("Failed to initialise multithreading in libgcrypt.\n");
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: gcry_control (pthread) failed - about to call MHD_PANIC\n");
      fprintf (f, "MHD_init: panic_msg_ptr=%p panic_msg=\"%s\"\n",
               (void *)mhd_msg, (mhd_msg != NULL) ? mhd_msg : "(null)");
      fclose (f);
    }
    MHD_PANIC ((char *)mhd_msg);
  }
#elif defined(MHD_W32_MUTEX_)
  if (0 != gcry_control (GCRYCTL_SET_THREAD_CBS,
                         &gcry_threads_w32))
  {
    const char *mhd_msg = _ ("Failed to initialise multithreading in libgcrypt.\n");
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: gcry_control (w32) failed - about to call MHD_PANIC\n");
      fprintf (f, "MHD_init: panic_msg_ptr=%p panic_msg=\"%s\"\n",
               (void *)mhd_msg, (mhd_msg != NULL) ? mhd_msg : "(null)");
      fclose (f);
    }
    MHD_PANIC ((char *)mhd_msg);
  }
#endif /* defined(MHD_W32_MUTEX_) */
#endif /* GNUTLS_VERSION_NUMBER <= 0x020b00 */
  gcry_check_version (NULL);
#else
  if (NULL == gcry_check_version ("1.6.0"))
  {
    const char *mhd_msg = _ ("libgcrypt is too old. MHD was compiled for " \
                  "libgcrypt 1.6.0 or newer.\n");
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: gcry_check_version failed - about to call MHD_PANIC\n");
      fprintf (f, "MHD_init: panic_msg_ptr=%p panic_msg=\"%s\"\n",
               (void *)mhd_msg, (mhd_msg != NULL) ? mhd_msg : "(null)");
      fclose (f);
    }
    MHD_PANIC ((char *)mhd_msg);
  }
#endif
#endif /* MHD_HTTPS_REQUIRE_GCRYPT */
  gnutls_global_init ();
#endif /* HTTPS_SUPPORT */
  MHD_monotonic_sec_counter_init ();
  MHD_send_init_static_vars_ ();
  MHD_init_mem_pools_ ();
  /* Check whether sizes were correctly detected by configure */
#ifdef _DEBUG
  if (1)
  {
    struct timeval tv;
    mhd_assert (sizeof(tv.tv_sec) == SIZEOF_STRUCT_TIMEVAL_TV_SEC);
  }
#endif /* _DEBUG */
  mhd_assert (sizeof(uint64_t) == SIZEOF_UINT64_T);

  /* Final log indicating MHD_init completed (if reached) */
  {
    FILE *f = fopen("/tmp/logs/2675.txt", "a");
    if (f)
    {
      fprintf (f, "MHD_init: completed\n");
      fclose (f);
    }
  }
}
