static void
MHD_ip_limit_del (struct MHD_Daemon *daemon,
                  const struct sockaddr_storage *addr,
                  socklen_t addrlen)
{
  struct MHD_IPCount search_key;
  struct MHD_IPCount *found_key;
  void **nodep;
  FILE *log = fopen("/tmp/logs/2585.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_ip_limit_del: daemon=%p addr=%p addrlen=%u\n",
            (void *)daemon, (const void *)addr, (unsigned)addrlen);
  }

  daemon = MHD_get_master (daemon);
  if (log)
  {
    fprintf(log, "After MHD_get_master: daemon=%p per_ip_connection_limit=%d\n",
            (void *)daemon,
            (int)daemon->per_ip_connection_limit);
  }

  /* Ignore if no connection limit assigned */
  if (0 == daemon->per_ip_connection_limit)
  {
    if (log)
    {
      fprintf(log, "Early return: per_ip_connection_limit == 0\n");
      fclose(log);
    }
    return;
  }
  /* Initialize search key */
  if (MHD_NO == MHD_ip_addr_to_key (addr,
                                    addrlen,
                                    &search_key))
  {
    if (log)
    {
      fprintf(log, "Early return: MHD_ip_addr_to_key returned MHD_NO\n");
      fclose(log);
    }
    return;
  }

  if (log)
  {
    fprintf(log, "Search key initialized. Calling MHD_ip_count_lock\n");
  }

  MHD_ip_count_lock (daemon);

  /* Search for the IP address */
  if (NULL == (nodep = tfind (&search_key,
                              &daemon->per_ip_connection_count,
                              &MHD_ip_addr_compare)))
  {
    if (log)
    {
      fprintf(log, "tfind returned NULL (failed to find previously-added IP)\n");
      fclose(log);
    }
    /* Something's wrong if we couldn't find an IP address
     * that was previously added */
    MHD_PANIC (_ ("Failed to find previously-added IP address.\n"));
  }

  if (log)
  {
    /* nodep is a void** as in the original code; log both nodep and *nodep */
    fprintf(log, "tfind returned nodep=%p, *nodep=%p\n",
            (void *)nodep,
            (void *)*nodep);
  }

  found_key = (struct MHD_IPCount *) *nodep;

  if (log)
  {
    fprintf(log, "found_key=%p count=%d\n",
            (void *)found_key,
            (int)found_key->count);
  }

  /* Validate existing count for IP address */
  if (0 == found_key->count)
  {
    if (log)
    {
      fprintf(log, "Panic: previously-added IP address had counter of zero.\n");
      fclose(log);
    }
    MHD_PANIC (_ ("Previously-added IP address had counter of zero.\n"));
  }

  /* Remove the node entirely if count reduces to 0 */
  if (0 == --found_key->count)
  {
    if (log)
    {
      fprintf(log, "Decremented count to zero for found_key=%p; about to tdelete, unlock, free\n",
              (void *)found_key);
    }
    tdelete (found_key,
             &daemon->per_ip_connection_count,
             &MHD_ip_addr_compare);
    MHD_ip_count_unlock (daemon);
    free (found_key);
    if (log)
    {
      fprintf(log, "Deletion/free complete for found_key=%p\n", (void *)found_key);
      fclose(log);
    }
  }
  else
  {
    if (log)
    {
      fprintf(log, "Decremented count, new value=%d for found_key=%p; unlocking only\n",
              (int)found_key->count,
              (void *)found_key);
      fclose(log);
    }
    MHD_ip_count_unlock (daemon);
  }
}
