_MHD_EXTERN enum MHD_Result
MHD_lookup_connection_value_n (struct MHD_Connection *connection,
                               enum MHD_ValueKind kind,
                               const char *key,
                               size_t key_size,
                               const char **value_ptr,
                               size_t *value_size_ptr)
{
  struct MHD_HTTP_Req_Header *pos;
  FILE *logf = fopen("/tmp/logs/65.txt", "a");

  if (logf)
  {
    fprintf(logf, "ENTER MHD_lookup_connection_value_n: connection=%p kind=%u key=%p key_size=%zu value_ptr=%p value_size_ptr=%p\n",
            (void *)connection, (unsigned)kind, (const void *)key, key_size,
            (const void *)value_ptr, (void *)value_size_ptr);
  }

  if (NULL == connection)
  {
    if (logf)
    {
      fprintf(logf, "EXIT MHD_lookup_connection_value_n: connection is NULL -> MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  if (logf)
  {
    fprintf(logf, "connection->rq.headers_received=%p\n",
            (void *)connection->rq.headers_received);
  }

  if (NULL == key)
  {
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      if (logf)
      {
        fprintf(logf,
                "loop(NULL key): pos=%p pos->next=%p pos->header=%p pos->header_size=%zu pos->kind=%u\n",
                (void *)pos, (void *)pos->next, (void *)pos->header,
                pos->header_size, (unsigned)pos->kind);
      }

      if ( (0 != (kind & pos->kind)) &&
           (NULL == pos->header) )
      {
        if (logf)
          fprintf(logf, "  -> break: matching kind and header == NULL\n");
        break;
      }
      else
      {
        if (logf)
          fprintf(logf, "  -> continue: condition not met (kind_match=%d header_is_null=%d)\n",
                  (int)(0 != (kind & pos->kind)), (int)(NULL == pos->header));
      }
    }
  }
  else
  {
    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      if (logf)
      {
        fprintf(logf,
                "loop(key): pos=%p pos->next=%p pos->header=%p pos->header_size=%zu pos->kind=%u key=%p key_size=%zu\n",
                (void *)pos, (void *)pos->next, (void *)pos->header,
                pos->header_size, (unsigned)pos->kind, (const void *)key, key_size);
      }

      if ( (0 != (kind & pos->kind)) &&
           (key_size == pos->header_size) &&
           ( (key == pos->header) ||
             (MHD_str_equal_caseless_bin_n_ (key,
                                             pos->header,
                                             key_size) ) ) )
      {
        if (logf)
          fprintf(logf, "  -> break: kind matched and header matched by pointer or content\n");
        break;
      }
      else
      {
        if (logf)
          fprintf(logf, "  -> continue: condition not met (kind_match=%d size_eq=%d ptr_eq=%d content_eq=%d)\n",
                  (int)(0 != (kind & pos->kind)), (int)(key_size == pos->header_size),
                  (int)(key == pos->header),
                  (int)( (key != pos->header) &&
                         MHD_str_equal_caseless_bin_n_ (key, pos->header, key_size) ) );
      }
    }
  }

  if (NULL == pos)
  {
    if (logf)
    {
      fprintf(logf, "EXIT MHD_lookup_connection_value_n: pos == NULL -> MHD_NO\n");
      fclose(logf);
    }
    return MHD_NO;
  }

  if (logf)
  {
    fprintf(logf, "pos found: pos=%p pos->header=%p pos->header_size=%zu pos->value=%p pos->value_size=%zu\n",
            (void *)pos, (void *)pos->header, pos->header_size,
            (void *)pos->value, pos->value_size);
  }

  if (NULL != value_ptr)
  {
    *value_ptr = pos->value;
    if (logf)
      fprintf(logf, "assigned *value_ptr = %p\n", (void *)pos->value);
  }

  if (NULL != value_size_ptr)
  {
    *value_size_ptr = pos->value_size;
    if (logf)
      fprintf(logf, "assigned *value_size_ptr = %zu\n", pos->value_size);
  }

  if (logf)
  {
    fprintf(logf, "EXIT MHD_lookup_connection_value_n: returning MHD_YES\n");
    fclose(logf);
  }

  return MHD_YES;
}
