_MHD_EXTERN enum MHD_Result
MHD_lookup_connection_value_n (struct MHD_Connection *connection,
                               enum MHD_ValueKind kind,
                               const char *key,
                               size_t key_size,
                               const char **value_ptr,
                               size_t *value_size_ptr)
{
  struct MHD_HTTP_Req_Header *pos;
  /* Logging probe to /tmp/logs/65.txt to capture control flow and state. */
  FILE *log = NULL;
  log = fopen("/tmp/logs/65.txt", "a");
  if (log)
  {
    fprintf(log, "ENTER: MHD_lookup_connection_value_n(connection=%p, kind=%u, key=%p, key_size=%zu, value_ptr=%p, value_size_ptr=%p)\n",
            (void *)connection, (unsigned)kind, (const void *)key, key_size, (void *)value_ptr, (void *)value_size_ptr);
    fflush(log);
  }

  if (NULL == connection)
  {
    if (log)
    {
      fprintf(log, "EXIT: connection is NULL -> MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }

  if (NULL == key)
  {
    if (log)
      fprintf(log, "Branch: key == NULL -> searching for header == NULL\n");

    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      if (log)
        fprintf(log, "  Inspect pos=%p: pos->kind=0x%08x, pos->header=%p\n",
                (void *)pos, (unsigned)pos->kind, (void *)pos->header);

      if ( (0 != (kind & pos->kind)) &&
           (NULL == pos->header) )
      {
        if (log)
          fprintf(log, "  Match (header NULL) at pos=%p -> breaking\n", (void *)pos);
        break;
      }
    }
  }
  else
  {
    if (log)
      fprintf(log, "Branch: key != NULL -> searching for matching header (size=%zu)\n", key_size);

    for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
    {
      int kind_matches = (0 != (kind & pos->kind));
      int size_matches = (key_size == pos->header_size);
      int ptr_eq = (key == pos->header);
      int str_eq = 0;

      if (log)
        fprintf(log, "  Inspect pos=%p: pos->kind=0x%08x, pos->header=%p, pos->header_size=%zu -> kind_matches=%d, size_matches=%d, ptr_eq=%d\n",
                (void *)pos, (unsigned)pos->kind, (void *)pos->header, pos->header_size, kind_matches, size_matches, ptr_eq);

      /* Preserve original short-circuit: only call MHD_str_equal_caseless_bin_n_ when necessary. */
      if (kind_matches && size_matches)
      {
        if (!ptr_eq)
        {
          /* Call the provided comparison function only when pointer inequality. */
          str_eq = MHD_str_equal_caseless_bin_n_ (key, pos->header, key_size);
          if (log)
            fprintf(log, "    Called MHD_str_equal_caseless_bin_n_(key, pos->header, %zu) -> %d\n", key_size, str_eq);
        }

        if ( (ptr_eq) || (str_eq) )
        {
          if (log)
            fprintf(log, "  Match at pos=%p -> breaking (ptr_eq=%d, str_eq=%d)\n", (void *)pos, ptr_eq, str_eq);
          break;
        }
      }
    }
  }

  if (NULL == pos)
  {
    if (log)
      fprintf(log, "EXIT: header not found -> MHD_NO\n");
    if (log)
      fclose(log);
    return MHD_NO;
  }

  if (log)
    fprintf(log, "FOUND: pos=%p, pos->value=%p, pos->value_size=%zu\n", (void *)pos, (void *)pos->value, pos->value_size);

  if (NULL != value_ptr)
    *value_ptr = pos->value;

  if (NULL != value_size_ptr)
    *value_size_ptr = pos->value_size;

  if (log)
  {
    fprintf(log, "EXIT: returning MHD_YES\n");
    fclose(log);
  }

  return MHD_YES;
}
