static bool
MHD_lookup_header_token_ci (const struct MHD_Connection *connection,
                            const char *header,
                            size_t header_len,
                            const char *token,
                            size_t token_len)
{
  struct MHD_HTTP_Req_Header *pos;
  FILE *f = NULL;

  /* Attempt to open log file for append. If this fails, proceed without logging. */
  f = fopen("/tmp/logs/3385.txt", "a");
  if (f)
  {
    fprintf(f, "Entered MHD_lookup_header_token_ci: connection=%p header=%p header_len=%zu token=%p token_len=%zu\n",
            (void *)connection, (const void *)header, header_len, (const void *)token, token_len);
    if (header != NULL && header_len > 0)
    {
      /* Print up to 256 chars of header for safety */
      fprintf(f, "  header (len %zu): %.*s\n", header_len,
              (int) (header_len < 256 ? header_len : 256), header);
    }
    if (token != NULL && token_len > 0)
    {
      fprintf(f, "  token (len %zu): %.*s\n", token_len,
              (int) (token_len < 256 ? token_len : 256), token);
    }
    fflush(f);
  }

  if ((NULL == connection) || (NULL == header) || (0 == header[0]) ||
      (NULL == token) || (0 == token[0]))
  {
    if (f)
    {
      fprintf(f, "Early exit: invalid parameters\n");
      fclose(f);
    }
    return false;
  }

  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (f)
    {
      fprintf(f, "Iterating header: pos=%p kind=0x%X header_size=%zu header=%p value=%p next=%p\n",
              (void *)pos, (unsigned) pos->kind, pos->header_size,
              (const void *)pos->header, (const void *)pos->value, (const void *)pos->next);
      if (pos->header != NULL && pos->header_size > 0)
      {
        fprintf(f, "  pos->header (len %zu): %.*s\n", pos->header_size,
                (int) (pos->header_size < 256 ? pos->header_size : 256), pos->header);
      }
      if (pos->value != NULL)
      {
        /* pos->value is expected to be a NUL-terminated C string for header values */
        fprintf(f, "  pos->value: %s\n", pos->value);
      }
      fflush(f);
    }

    if ((0 != (pos->kind & MHD_HEADER_KIND)) &&
        (header_len == pos->header_size) &&
        ( (header == pos->header) ||
          (MHD_str_equal_caseless_bin_n_ (header,
                                          pos->header,
                                          header_len)) ) &&
        (MHD_str_has_token_caseless_ (pos->value, token, token_len)))
    {
      if (f)
      {
        fprintf(f, "Match found at pos=%p\n", (void *)pos);
        fclose(f);
      }
      return true;
    }
    else
    {
      if (f)
      {
        fprintf(f, "No match at pos=%p\n", (void *)pos);
        fflush(f);
      }
    }
  }

  if (f)
  {
    fprintf(f, "No matching header found\n");
    fclose(f);
  }
  return false;
}
