static bool
MHD_lookup_header_token_ci (const struct MHD_Connection *connection,
                            const char *header,
                            size_t header_len,
                            const char *token,
                            size_t token_len)
{
  struct MHD_HTTP_Req_Header *pos;
  FILE *fp = fopen("/tmp/logs/3385.txt", "a");

  if (fp)
  {
    fprintf(fp, "ENTER MHD_lookup_header_token_ci: this=%p connection=%p header=%p header_len=%zu token=%p token_len=%zu\n",
            (void *)MHD_lookup_header_token_ci, (const void *)connection,
            (const void *)header, header_len, (const void *)token, token_len);
    if (connection)
      fprintf(fp, " connection->rq.headers_received=%p\n", (void *)connection->rq.headers_received);
    if (header)
      fprintf(fp, " header[0]=%d\n", (int)header[0]);
    else
      fprintf(fp, " header is NULL\n");
    if (token)
      fprintf(fp, " token[0]=%d\n", (int)token[0]);
    else
      fprintf(fp, " token is NULL\n");
  }

  if ((NULL == connection) || (NULL == header) || (0 == header[0]) ||
      (NULL == token) || (0 == token[0]))
  {
    if (fp)
    {
      fprintf(fp, "EARLY RETURN false: invalid args. connection=%p header=%p token=%p\n",
              (const void *)connection, (const void *)header, (const void *)token);
      fclose(fp);
    }
    return false;
  }

  int idx = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (fp)
    {
      fprintf(fp, "LOOP idx=%d pos=%p pos->kind=0x%08x pos->header_size=%zu pos->header=%p pos->value=%p pos->next=%p\n",
              idx, (void *)pos, (unsigned)pos->kind, pos->header_size,
              (void *)pos->header, (void *)pos->value, (void *)pos->next);
      if (pos->header && pos->header_size)
      {
        size_t show = pos->header_size > 64 ? 64 : pos->header_size;
        fprintf(fp, "  pos->header (first %zu of %zu) = %.*s\n", show, pos->header_size, (int)show, pos->header);
      }
      else
        fprintf(fp, "  pos->header is NULL or empty\n");
    }

    if ((0 != (pos->kind & MHD_HEADER_KIND)) &&
        (header_len == pos->header_size))
    {
      bool header_matches = false;

      if (header == pos->header)
      {
        header_matches = true;
        if (fp) fprintf(fp, "  Header pointers are identical (header == pos->header)\n");
      }
      else
      {
        if (fp) fprintf(fp, "  Calling MHD_str_equal_caseless_bin_n_ with header=%p pos->header=%p header_len=%zu\n",
                        (const void *)header, (const void *)pos->header, header_len);
        header_matches = MHD_str_equal_caseless_bin_n_ (header, pos->header, header_len);
        if (fp) fprintf(fp, "  MHD_str_equal_caseless_bin_n_ returned %d\n", header_matches);
      }

      if (header_matches)
      {
        if (fp) fprintf(fp, "  Header matched; about to call MHD_str_has_token_caseless_ with pos->value=%p token=%p token_len=%zu\n",
                        (const void *)pos->value, (const void *)token, token_len);

        bool token_found = MHD_str_has_token_caseless_ (pos->value, token, token_len);

        if (fp) fprintf(fp, "  MHD_str_has_token_caseless_ returned %d\n", token_found);

        if (token_found)
        {
          if (fp) { fprintf(fp, "MATCH FOUND: returning true (idx=%d pos=%p)\n", idx, (void *)pos); fclose(fp); }
          return true;
        }
      }
    }
    else
    {
      if (fp)
        fprintf(fp, "  Skipping pos: kind or size mismatch (kind & MHD_HEADER_KIND == %s, header_len==pos->header_size == %s)\n",
                (0 != (pos->kind & MHD_HEADER_KIND)) ? "true" : "false",
                (header_len == pos->header_size) ? "true" : "false");
    }

    idx++;
  }

  if (fp)
  {
    fprintf(fp, "NO MATCH: returning false\n");
    fclose(fp);
  }

  return false;
}
