static bool
MHD_lookup_header_token_ci (const struct MHD_Connection *connection,
                            const char *header,
                            size_t header_len,
                            const char *token,
                            size_t token_len)
{
  /* Probe log file to capture control flow and state for debugging. */
  FILE *fp = fopen("/tmp/logs/3385.txt", "a");

  struct MHD_HTTP_Req_Header *pos;

  if (fp)
    fprintf(fp, "ENTER: MHD_lookup_header_token_ci(connection=%p, header=%p, header_len=%zu, token=%p, token_len=%zu)\n",
            (const void *)connection, (const void *)header, header_len, (const void *)token, token_len);

  if ((NULL == connection) || (NULL == header) || (0 == header[0]) ||
      (NULL == token) || (0 == token[0]))
  {
    if (fp)
      fprintf(fp, "EARLY_EXIT: invalid argument(s): connection=%p header=%p token=%p\n",
              (const void *)connection, (const void *)header, (const void *)token);
    if (fp)
      fclose(fp);
    return false;
  }

  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next)
  {
    if (fp)
    {
      fprintf(fp,
              "LOOP: pos=%p pos->header=%p pos->header_size=%zu pos->kind=0x%08x pos->value=%p pos->next=%p\n",
              (const void *)pos,
              (const void *)pos->header,
              pos->header_size,
              (unsigned)pos->kind,
              (const void *)pos->value,
              (const void *)pos->next);
    }

    /* Evaluate subconditions and log them so callers can reproduce the
       sequence of evaluations that leads to a match. */
    bool cond_kind = (0 != (pos->kind & MHD_HEADER_KIND));
    bool cond_header_len = (header_len == pos->header_size);
    bool cond_header_equal = ((header == pos->header) ||
                              (MHD_str_equal_caseless_bin_n_ (header,
                                                              pos->header,
                                                              header_len)));
    bool cond_token = MHD_str_has_token_caseless_ (pos->value, token, token_len);

    if (fp)
      fprintf(fp, "COND: kind=%d header_len_match=%d header_equal=%d token=%d\n",
              cond_kind ? 1 : 0,
              cond_header_len ? 1 : 0,
              cond_header_equal ? 1 : 0,
              cond_token ? 1 : 0);

    if (cond_kind && cond_header_len && cond_header_equal && cond_token)
    {
      if (fp)
        fprintf(fp, "FOUND: match at pos=%p -> returning true\n", (const void *)pos);
      if (fp)
        fclose(fp);
      return true;
    }
  }

  if (fp)
    fprintf(fp, "NOT_FOUND: no matching header -> returning false\n");
  if (fp)
    fclose(fp);
  return false;
}
