uint64_t
MHD_monotonic_msec_counter (void)
{
#if defined(HAVE_CLOCK_GETTIME) || defined(HAVE_TIMESPEC_GET)
  struct timespec ts;
#endif /* HAVE_CLOCK_GETTIME || HAVE_TIMESPEC_GET */

#ifdef HAVE_CLOCK_GETTIME
  {
    /* Log entry to help reproduce branch reachability */
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "MHD_monotonic_msec_counter: ENTER (HAVE_CLOCK_GETTIME block)\n");
      fprintf(logf, "  mono_clock_id=%d, _MHD_UNWANTED_CLOCK=%d, mono_clock_start=%llu\n",
              (int) mono_clock_id, (int) _MHD_UNWANTED_CLOCK,
              (unsigned long long) mono_clock_start);
      fclose(logf);
    }
  }
  if ( (_MHD_UNWANTED_CLOCK != mono_clock_id) )
  {
    int _cg_ret = clock_gettime (mono_clock_id, &ts);
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "clock_gettime called: ret=%d, ts.tv_sec=%lld, ts.tv_nsec=%lld\n",
              _cg_ret, (long long) ts.tv_sec, (long long) ts.tv_nsec);
      fclose(logf);
    }
    if (0 == _cg_ret)
    {
      uint64_t _ret = (uint64_t) (((uint64_t) (ts.tv_sec - mono_clock_start)) * 1000
                       + (uint64_t) (ts.tv_nsec / 1000000));
      logf = fopen("/tmp/logs/1696.txt", "a");
      if (logf) {
        fprintf(logf, "Returning from CLOCK_GETTIME branch: %llu ms\n", (unsigned long long) _ret);
        fclose(logf);
      }
      return _ret;
    }
  }
#endif /* HAVE_CLOCK_GETTIME */
#ifdef HAVE_CLOCK_GET_TIME
  if (_MHD_INVALID_CLOCK_SERV != mono_clock_service)
  {
    mach_timespec_t cur_time;

    if (KERN_SUCCESS == clock_get_time (mono_clock_service,
                                        &cur_time))
    {
      uint64_t _ret = (uint64_t) (((uint64_t) (cur_time.tv_sec - mono_clock_start))
                         * 1000 + (uint64_t) (cur_time.tv_nsec / 1000000));
      FILE *logf = fopen("/tmp/logs/1696.txt", "a");
      if (logf) {
        fprintf(logf, "Returning from CLOCK_GET_TIME branch: cur_time.tv_sec=%lld, cur_time.tv_nsec=%lld, ret=%llu\n",
                (long long) cur_time.tv_sec, (long long) cur_time.tv_nsec, (unsigned long long) _ret);
        fclose(logf);
      }
      return _ret;
    }
    else {
      FILE *logf = fopen("/tmp/logs/1696.txt", "a");
      if (logf) {
        fprintf(logf, "clock_get_time failed (not KERN_SUCCESS)\n");
        fclose(logf);
      }
    }
  }
#endif /* HAVE_CLOCK_GET_TIME */
#if defined(_WIN32)
#if _WIN32_WINNT >= 0x0600
  {
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "WIN32 GetTickCount64 branch: tick_start=%llu\n", (unsigned long long) tick_start);
      fclose(logf);
    }
  }
  if (1)
    return (uint64_t) (GetTickCount64 () - tick_start);
#else  /* _WIN32_WINNT < 0x0600 */
  if (0 != perf_freq)
  {
    LARGE_INTEGER perf_counter;
    uint64_t num_ticks;

    QueryPerformanceCounter (&perf_counter);   /* never fail on XP and later */
    num_ticks = (uint64_t) (perf_counter.QuadPart - perf_start);
    {
      uint64_t _ret = ((num_ticks / perf_freq) * 1000)
           + ((num_ticks % perf_freq) / (perf_freq / 1000));
      FILE *logf = fopen("/tmp/logs/1696.txt", "a");
      if (logf) {
        fprintf(logf, "WIN32 QueryPerformanceCounter branch: num_ticks=%llu, perf_freq=%llu, ret=%llu\n",
                (unsigned long long) num_ticks, (unsigned long long) perf_freq,
                (unsigned long long) _ret);
        fclose(logf);
      }
      return _ret;
    }
  }
#endif /* _WIN32_WINNT < 0x0600 */
#endif /* _WIN32 */
#ifdef HAVE_GETHRTIME
  {
    uint64_t _ret = ((uint64_t) (gethrtime () - hrtime_start)) / 1000000;
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "HAVE_GETHRTIME branch taken: hrtime_start=%llu, ret=%llu\n",
              (unsigned long long) hrtime_start, (unsigned long long) _ret);
      fclose(logf);
    }
    return _ret;
  }
#endif /* HAVE_GETHRTIME */

  /* Fallbacks, affected by system time change */
#ifdef HAVE_TIMESPEC_GET
  {
    int _tg = timespec_get (&ts, TIME_UTC);
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "timespec_get returned %d, ts.tv_sec=%lld, ts.tv_nsec=%lld\n", _tg, (long long) ts.tv_sec, (long long) ts.tv_nsec);
      fclose(logf);
    }
    if (TIME_UTC == _tg)
    {
      uint64_t _ret = (uint64_t) (((uint64_t) (ts.tv_sec - gettime_start)) * 1000
                       + (uint64_t) (ts.tv_nsec / 1000000));
      FILE *logf2 = fopen("/tmp/logs/1696.txt", "a");
      if (logf2) {
        fprintf(logf2, "Returning from TIMESPEC_GET branch: ret=%llu\n", (unsigned long long) _ret);
        fclose(logf2);
      }
      return _ret;
    }
  }
#elif defined(HAVE_GETTIMEOFDAY)
  if (1)
  {
    struct timeval tv;
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "Entering GETTIMEOFDAY fallback branch\n");
      fclose(logf);
    }
    if (0 == gettimeofday (&tv, NULL))
    {
      uint64_t _ret = (uint64_t) (((uint64_t) (tv.tv_sec - gettime_start)) * 1000
                         + (uint64_t) (tv.tv_usec / 1000));
      FILE *logf2 = fopen("/tmp/logs/1696.txt", "a");
      if (logf2) {
        fprintf(logf2, "Returning from GETTIMEOFDAY branch: tv_sec=%lld, tv_usec=%ld, ret=%llu\n",
                (long long) tv.tv_sec, (long) tv.tv_usec, (unsigned long long) _ret);
        fclose(logf2);
      }
      return _ret;
    }
    else {
      FILE *logf2 = fopen("/tmp/logs/1696.txt", "a");
      if (logf2) {
        fprintf(logf2, "gettimeofday failed\n");
        fclose(logf2);
      }
    }
  }
#endif /* HAVE_GETTIMEOFDAY */

  /* The last resort fallback with very low resolution */
  {
    uint64_t _ret = (uint64_t) (time (NULL) - sys_clock_start) * 1000;
    FILE *logf = fopen("/tmp/logs/1696.txt", "a");
    if (logf) {
      fprintf(logf, "Final fallback (time(NULL)) used: sys_clock_start=%lld, ret=%llu\n",
              (long long) sys_clock_start, (unsigned long long) _ret);
      fclose(logf);
    }
    return _ret;
  }
}
