enum MHD_Result
MHD_parse_arguments_ (struct MHD_Connection *connection,
                      enum MHD_ValueKind kind,
                      char *args,
                      MHD_ArgumentIterator_ cb,
                      void *cls)
{
  struct MHD_Daemon *daemon = connection->daemon;
  char *equals;
  char *amper;
  FILE *log = fopen("/tmp/logs/4510.txt", "a");

  if (log)
  {
    /* Log entry and key runtime pointers/state */
    fprintf(log, "ENTER MHD_parse_arguments_: connection=%p kind=%d args=%p cb=%p cls=%p daemon=%p\n",
            (void *)connection, (int)kind, (void *)args, (void *)cb, (void *)cls, (void *)daemon);
    /* Detect if the cb pointer equals this function pointer (direct recursion via callback) */
    if (cb == (MHD_ArgumentIterator_)MHD_parse_arguments_)
      fprintf(log, "NOTICE: cb pointer equals MHD_parse_arguments_ (direct recursion via callback possible)\n");
    fflush(log);
  }

  while ( (NULL != args) &&
          ('\0' != args[0]) )
  {
    size_t key_len;
    size_t value_len;
    equals = strchr (args, '=');
    amper = strchr (args, '&');
    if (NULL == amper)
    {
      /* last argument */
      if (NULL == equals)
      {
        /* last argument, without '=' */
        MHD_unescape_plus (args);
        key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             args);
        if (log)
        {
          fprintf(log, "About to invoke cb (last arg without '='): cb=%p cls=%p key='%s' key_len=%zu value=NULL value_len=0 kind=%d\n",
                  (void *)cb, (void *)cls, args ? args : "(null)", key_len, (int)kind);
          fflush(log);
        }
        if (MHD_NO == cb (cls,
                          args,
                          key_len,
                          NULL,
                          0,
                          kind))
        {
          if (log)
          {
            fprintf(log, "cb returned MHD_NO (aborting)\n");
            fclose(log);
          }
          return MHD_NO;
        }
        break;
      }
      /* got 'foo=bar' */
      equals[0] = '\0';
      equals++;
      MHD_unescape_plus (args);
      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);
      MHD_unescape_plus (equals);
      value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             equals);
      if (log)
      {
        fprintf(log, "About to invoke cb (last 'foo=bar'): cb=%p cls=%p key='%s' key_len=%zu value='%s' value_len=%zu kind=%d\n",
                (void *)cb, (void *)cls, args ? args : "(null)", key_len, equals ? equals : "(null)", value_len, (int)kind);
        fflush(log);
      }
      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        equals,
                        value_len,
                        kind))
      {
        if (log)
        {
          fprintf(log, "cb returned MHD_NO (aborting)\n");
          fclose(log);
        }
        return MHD_NO;
      }
      break;
    }
    /* amper is non-NULL here */
    amper[0] = '\0';
    amper++;
    if ( (NULL == equals) ||
         (equals >= amper) )
    {
      /* got 'foo&bar' or 'foo&bar=val', add key 'foo' with NULL for value */
      MHD_unescape_plus (args);
      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);
      if (log)
      {
        fprintf(log, "About to invoke cb (no '=' before '&'): cb=%p cls=%p key='%s' key_len=%zu value=NULL value_len=0 kind=%d\n",
                (void *)cb, (void *)cls, args ? args : "(null)", key_len, (int)kind);
        fflush(log);
      }
      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        NULL,
                        0,
                        kind))
      {
        if (log)
        {
          fprintf(log, "cb returned MHD_NO (aborting)\n");
          fclose(log);
        }
        return MHD_NO;
      }
      /* continue with 'bar' */
      args = amper;
      continue;
    }
    /* equals and amper are non-NULL here, and equals < amper,
 so we got regular 'foo=value&bar...'-kind of argument */
    equals[0] = '\0';
    equals++;
    MHD_unescape_plus (args);
    key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                         connection,
                                         args);
    MHD_unescape_plus (equals);
    value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           equals);
    if (log)
    {
      fprintf(log, "About to invoke cb (regular 'foo=value'): cb=%p cls=%p key='%s' key_len=%zu value='%s' value_len=%zu kind=%d\n",
              (void *)cb, (void *)cls, args ? args : "(null)", key_len, equals ? equals : "(null)", value_len, (int)kind);
      fflush(log);
    }
    if (MHD_NO == cb (cls,
                      args,
                      key_len,
                      equals,
                      value_len,
                      kind))
    {
      if (log)
      {
        fprintf(log, "cb returned MHD_NO (aborting)\n");
        fclose(log);
      }
      return MHD_NO;
    }
    args = amper;
  }
  if (log)
  {
    fprintf(log, "EXIT MHD_parse_arguments_: returning MHD_YES\n");
    fclose(log);
  }
  return MHD_YES;
}
