enum MHD_Result
MHD_parse_arguments_ (struct MHD_Connection *connection,
                      enum MHD_ValueKind kind,
                      char *args,
                      MHD_ArgumentIterator_ cb,
                      void *cls)
{
  struct MHD_Daemon *daemon = connection->daemon;
  char *equals;
  char *amper;

  /* Log entry into the function */
  {
    FILE *fp = fopen("/tmp/logs/4512.txt", "a");
    if (fp)
    {
      if (args)
        fprintf(fp, "ENTER MHD_parse_arguments_: connection=%p kind=%d args=%p args-start='%.128s'\n",
                (void *)connection, (int)kind, (void *)args, args);
      else
        fprintf(fp, "ENTER MHD_parse_arguments_: connection=%p kind=%d args=NULL\n",
                (void *)connection, (int)kind);
      fclose(fp);
    }
  }

  while ( (NULL != args) &&
          ('\0' != args[0]) )
  {
    size_t key_len;
    size_t value_len;
    equals = strchr (args, '=');
    amper = strchr (args, '&');

    /* Log positions of equals and amper for this iteration */
    {
      FILE *fp = fopen("/tmp/logs/4512.txt", "a");
      if (fp)
      {
        fprintf(fp, "LOOP: args=%p '%.128s' equals=%p amper=%p\n",
                (void *)args, args, (void *)equals, (void *)amper);
        if ((NULL != equals) && (NULL != args))
          fprintf(fp, "  equals offset=%td\n", (ptrdiff_t)(equals - args));
        if ((NULL != amper) && (NULL != args))
          fprintf(fp, "  amper offset=%td\n", (ptrdiff_t)(amper - args));
        fclose(fp);
      }
    }

    if (NULL == amper)
    {
      /* last argument */
      if (NULL == equals)
      {
        /* last argument, without '=' */
        MHD_unescape_plus (args);
        key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             args);
        if (MHD_NO == cb (cls,
                          args,
                          key_len,
                          NULL,
                          0,
                          kind))
        {
          FILE *fp = fopen("/tmp/logs/4512.txt", "a");
          if (fp)
          {
            fprintf(fp, "CALLBACK returned MHD_NO for key '%.128s' (no value)\n", args);
            fclose(fp);
          }
          return MHD_NO;
        }
        break;
      }
      /* got 'foo=bar' */
      equals[0] = '\0';
      equals++;

      /* Log split for key/value when no amper present */
      {
        FILE *fp = fopen("/tmp/logs/4512.txt", "a");
        if (fp)
        {
          fprintf(fp, "SPLIT (no amper): key=");
          if (args) fprintf(fp, "'%.128s' ", args); else fprintf(fp, "NULL ");
          fprintf(fp, " value=%p '%.128s'\n", (void *)equals, equals);
          fclose(fp);
        }
      }

      MHD_unescape_plus (args);
      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);
      MHD_unescape_plus (equals);
      value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             equals);
      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        equals,
                        value_len,
                        kind))
      {
        FILE *fp = fopen("/tmp/logs/4512.txt", "a");
        if (fp)
        {
          fprintf(fp, "CALLBACK returned MHD_NO for key '%.128s' value '%.128s'\n", args, equals);
          fclose(fp);
        }
        return MHD_NO;
      }
      break;
    }
    /* amper is non-NULL here */
    amper[0] = '\0';
    amper++;

    if ( (NULL == equals) ||
         (equals >= amper) )
    {
      /* got 'foo&bar' or 'foo&bar=val', add key 'foo' with NULL for value */
      MHD_unescape_plus (args);
      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);
      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        NULL,
                        0,
                        kind))
      {
        FILE *fp = fopen("/tmp/logs/4512.txt", "a");
        if (fp)
        {
          fprintf(fp, "CALLBACK returned MHD_NO for key '%.128s' (no value) before advancing to next arg\n", args);
          fclose(fp);
        }
        return MHD_NO;
      }
      /* continue with 'bar' */

      /* Log the pointer reassignment that is relevant to MISRA Rule 17.8 */
      {
        FILE *fp = fopen("/tmp/logs/4512.txt", "a");
        if (fp)
        {
          fprintf(fp, "ABOUT TO REASSIGN args (pointer parameter) from %p '%.128s' to amper %p '%.128s'\n",
                  (void *)args, args ? args : "(null)", (void *)amper, amper ? amper : "(null)");
          fclose(fp);
        }
      }

      args = amper;

      /* Log after reassignment */
      {
        FILE *fp = fopen("/tmp/logs/4512.txt", "a");
        if (fp)
        {
          fprintf(fp, "AFTER REASSIGN args=%p '%.128s'\n", (void *)args, args ? args : "(null)");
          fclose(fp);
        }
      }

      continue;
    }
    /* equals and amper are non-NULL here, and equals < amper,
 so we got regular 'foo=value&bar...'-kind of argument */
    equals[0] = '\0';
    equals++;
    MHD_unescape_plus (args);
    key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                         connection,
                                         args);
    MHD_unescape_plus (equals);
    value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           equals);
    if (MHD_NO == cb (cls,
                      args,
                      key_len,
                      equals,
                      value_len,
                      kind))
    {
      FILE *fp = fopen("/tmp/logs/4512.txt", "a");
      if (fp)
      {
        fprintf(fp, "CALLBACK returned MHD_NO for key '%.128s' value '%.128s' (regular case)\n", args, equals);
        fclose(fp);
      }
      return MHD_NO;
    }

    /* Log the pointer reassignment that is relevant to MISRA Rule 17.8 */
    {
      FILE *fp = fopen("/tmp/logs/4512.txt", "a");
      if (fp)
      {
        fprintf(fp, "ABOUT TO REASSIGN args (pointer parameter) from %p '%.128s' to amper %p '%.128s' (regular case)\n",
                (void *)args, args ? args : "(null)", (void *)amper, amper ? amper : "(null)");
        fclose(fp);
      }
    }

    args = amper;

    /* Log after reassignment */
    {
      FILE *fp = fopen("/tmp/logs/4512.txt", "a");
      if (fp)
      {
        fprintf(fp, "AFTER REASSIGN args=%p '%.128s'\n", (void *)args, args ? args : "(null)");
        fclose(fp);
      }
    }
  }

  /* Log exit */
  {
    FILE *fp = fopen("/tmp/logs/4512.txt", "a");
    if (fp)
    {
      fprintf(fp, "EXIT MHD_parse_arguments_: returning MHD_YES\n");
      fclose(fp);
    }
  }

  return MHD_YES;
}
