enum MHD_Result
MHD_parse_arguments_ (struct MHD_Connection *connection,
                      enum MHD_ValueKind kind,
                      char *args,
                      MHD_ArgumentIterator_ cb,
                      void *cls)
{
  /* preserve original non-const pointer (this is the reported violation point) */
  struct MHD_Daemon *daemon = connection->daemon;
  char *equals;
  char *amper;
  FILE *log = fopen("/tmp/logs/4515.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER: MHD_parse_arguments_: connection=%p, daemon=%p, args=%p, kind=%d, cb=%p, cls=%p\n",
            (void *)connection, (void *)daemon, (void *)args, (int)kind, (void *)cb, cls);
    fflush(log);
  }

  while ( (NULL != args) &&
          ('\0' != args[0]) )
  {
    size_t key_len;
    size_t value_len;
    equals = strchr (args, '=');
    amper = strchr (args, '&');

    if (log)
    {
      fprintf(log, "LOOP: args=%p, first_char='%c', equals=%p, amper=%p\n",
              (void *)args, (args && args[0]) ? args[0] : '?', (void *)equals, (void *)amper);
      fflush(log);
    }

    if (NULL == amper)
    {
      /* last argument */
      if (NULL == equals)
      {
        /* last argument, without '=' */
        if (log)
        {
          fprintf(log, "BRANCH: last argument without '='; args before unescape='%s'\n", args);
          fflush(log);
        }

        MHD_unescape_plus (args);

        if (log)
        {
          fprintf(log, "AFTER unescape: args=%s\n", args);
          fflush(log);
        }

        key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             args);

        if (log)
        {
          fprintf(log, "CALL: unescape_callback returned key_len=%zu (daemon=%p, cls=%p)\n",
                  key_len, (void *)daemon, (void *)daemon->unescape_callback_cls);
          fflush(log);
        }

        if (MHD_NO == cb (cls,
                          args,
                          key_len,
                          NULL,
                          0,
                          kind))
        {
          if (log)
          {
            fprintf(log, "RETURN: cb returned MHD_NO for key '%s' (key_len=%zu)\n", args, key_len);
            fclose(log);
          }
          return MHD_NO;
        }
        break;
      }
      /* got 'foo=bar' */
      equals[0] = '\0';
      equals++;

      if (log)
      {
        fprintf(log, "BRANCH: last 'foo=bar' pair; key_before_unescape='%s', value_ptr=%p\n", args, (void *)equals);
        fflush(log);
      }

      MHD_unescape_plus (args);

      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);

      MHD_unescape_plus (equals);

      value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                             connection,
                                             equals);

      if (log)
      {
        fprintf(log, "CALL: unescape_callback key_len=%zu, value_len=%zu; key='%s', value='%s'\n",
                key_len, value_len, args, equals);
        fflush(log);
      }

      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        equals,
                        value_len,
                        kind))
      {
        if (log)
        {
          fprintf(log, "RETURN: cb returned MHD_NO for key '%s' value '%s'\n", args, equals);
          fclose(log);
        }
        return MHD_NO;
      }
      break;
    }
    /* amper is non-NULL here */
    amper[0] = '\0';
    amper++;

    if (log)
    {
      fprintf(log, "BRANCH: amper non-NULL; split into '%s' and next part at %p\n", args, (void *)amper);
      fflush(log);
    }

    if ( (NULL == equals) ||
         (equals >= amper) )
    {
      /* got 'foo&bar' or 'foo&bar=val', add key 'foo' with NULL for value */
      MHD_unescape_plus (args);

      if (log)
      {
        fprintf(log, "CASE: key without value after unescape: '%s'\n", args);
        fflush(log);
      }

      key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           args);

      if (log)
      {
        fprintf(log, "CALL: unescape_callback returned key_len=%zu for key '%s'\n", key_len, args);
        fflush(log);
      }

      if (MHD_NO == cb (cls,
                        args,
                        key_len,
                        NULL,
                        0,
                        kind))
      {
        if (log)
        {
          fprintf(log, "RETURN: cb returned MHD_NO for key '%s' (no value)\n", args);
          fclose(log);
        }
        return MHD_NO;
      }
      /* continue with 'bar' */
      args = amper;

      if (log)
      {
        fprintf(log, "CONTINUE: next args=%p ('%s')\n", (void *)args, args);
        fflush(log);
      }

      continue;
    }
    /* equals and amper are non-NULL here, and equals < amper,
 so we got regular 'foo=value&bar...'-kind of argument */
    equals[0] = '\0';
    equals++;

    if (log)
    {
      fprintf(log, "CASE: regular 'foo=value' with key='%s' value_at=%p\n", args, (void *)equals);
      fflush(log);
    }

    MHD_unescape_plus (args);

    key_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                         connection,
                                         args);

    MHD_unescape_plus (equals);

    value_len = daemon->unescape_callback (daemon->unescape_callback_cls,
                                           connection,
                                           equals);

    if (log)
    {
      fprintf(log, "CALL: unescape_callback key_len=%zu, value_len=%zu; key='%s', value='%s'\n",
              key_len, value_len, args, equals);
      fflush(log);
    }

    if (MHD_NO == cb (cls,
                      args,
                      key_len,
                      equals,
                      value_len,
                      kind))
    {
      if (log)
      {
        fprintf(log, "RETURN: cb returned MHD_NO for key '%s' value '%s'\n", args, equals);
        fclose(log);
      }
      return MHD_NO;
    }
    args = amper;
  }

  if (log)
  {
    fprintf(log, "EXIT: MHD_parse_arguments_ returning MHD_YES\n");
    fclose(log);
  }
  return MHD_YES;
}
