static enum MHD_Result
MHD_poll_all (struct MHD_Daemon *daemon,
              int32_t millisec)
{
  unsigned int num_connections;
  struct MHD_Connection *pos;
  struct MHD_Connection *prev;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  enum MHD_Result result = MHD_YES;
  FILE *logf = NULL;

  /* Open log file early so we can trace control flow and state. */
  logf = fopen("/tmp/logs/842.txt", "a");
  if (logf)
  {
    (void) fprintf(logf, "ENTER MHD_poll_all: daemon=%p millisec=%d\n", (void *) daemon, (int) millisec);
    (void) fflush(logf);
  }

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  if (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME))
  {
    enum MHD_Result rs = resume_suspended_connections (daemon);
    if (MHD_NO != rs)
    {
      if (logf)
      {
        (void) fprintf(logf, "resume_suspended_connections -> %d, millisec before assign=%d\n", (int) rs, (int) millisec);
        (void) fflush(logf);
      }
      /* This assignment is the reported MISRA Rule 17.8 violation. */
      millisec = 0;
      if (logf)
      {
        (void) fprintf(logf, "millisec after assign=%d\n", (int) millisec);
        (void) fflush(logf);
      }
    }
  }

  /* count number of connections and thus determine poll set size */
  num_connections = 0;
  for (pos = daemon->connections_head; NULL != pos; pos = pos->next)
    num_connections++;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  for (urh = daemon->urh_head; NULL != urh; urh = urh->next)
    num_connections += 2;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  {
    unsigned int i;
    int timeout;
    unsigned int poll_server;
    int poll_listen;
    int poll_itc_idx;
    struct pollfd *p;
    MHD_socket ls;

    p = MHD_calloc_ ((2 + (size_t) num_connections),
                     sizeof (struct pollfd));
    if (NULL == p)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Error allocating memory: %s\n"),
                MHD_strerror_ (errno));
#endif
      if (logf)
      {
        (void) fprintf(logf, "calloc failed for %u connections\n", num_connections);
        (void) fflush(logf);
      }
      result = MHD_NO;
      goto close_log;
    }
    if (logf)
    {
      (void) fprintf(logf, "allocated pollfd array for %u connections (p=%p)\n", num_connections, (void *) p);
      (void) fflush(logf);
    }

    poll_server = 0;
    poll_listen = -1;
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) &&
         (daemon->connections < daemon->connection_limit) &&
         (! daemon->at_limit) )
    {
      /* only listen if we are not at the connection limit */
      p[poll_server].fd = ls;
      p[poll_server].events = POLLIN;
      p[poll_server].revents = 0;
      poll_listen = (int) poll_server;
      poll_server++;
      if (logf)
      {
        (void) fprintf(logf, "added listen fd %d at poll index %u\n", (int) ls, poll_server - 1);
        (void) fflush(logf);
      }
    }
    poll_itc_idx = -1;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      p[poll_server].fd = MHD_itc_r_fd_ (daemon->itc);
      p[poll_server].events = POLLIN;
      p[poll_server].revents = 0;
      poll_itc_idx = (int) poll_server;
      poll_server++;
      if (logf)
      {
        (void) fprintf(logf, "added ITC fd %d at poll index %u\n", (int) p[poll_itc_idx].fd, poll_itc_idx);
        (void) fflush(logf);
      }
    }

    timeout = get_timeout_millisec_int (daemon, millisec);
    if (logf)
    {
      (void) fprintf(logf, "computed timeout=%d for millisec=%d\n", timeout, (int) millisec);
      (void) fflush(logf);
    }

    i = 0;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      p[poll_server + i].fd = pos->socket_fd;
      switch (pos->event_loop_info)
      {
      case MHD_EVENT_LOOP_INFO_READ:
      case MHD_EVENT_LOOP_INFO_PROCESS_READ:
        p[poll_server + i].events |= POLLIN | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_WRITE:
        p[poll_server + i].events |= POLLOUT | MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_PROCESS:
        p[poll_server + i].events |=  MHD_POLL_EVENTS_ERR_DISC;
        break;
      case MHD_EVENT_LOOP_INFO_CLEANUP:
        timeout = 0; /* clean up "pos" immediately */
        break;
      }
      if (logf)
      {
        (void) fprintf(logf, "added conn fd=%d at poll index=%u events=0x%x\n", (int) pos->socket_fd, poll_server + i, (unsigned int) p[poll_server + i].events);
        (void) fflush(logf);
      }
      i++;
    }
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    for (urh = daemon->urh_tail; NULL != urh; urh = urh->prev)
    {
      urh_to_pollfd (urh, &(p[poll_server + i]));
      i += 2;
    }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
    if (0 == poll_server + num_connections)
    {
      free (p);
      if (logf)
      {
        (void) fprintf(logf, "no fds to poll, exiting with MHD_YES\n");
        (void) fflush(logf);
      }
      result = MHD_YES;
      goto close_log;
    }
    if (MHD_sys_poll_ (p,
                       poll_server + num_connections,
                       timeout) < 0)
    {
      const int err = MHD_socket_get_error_ ();
      if (logf)
      {
        (void) fprintf(logf, "poll failed err=%d\n", err);
        (void) fflush(logf);
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        free (p);
        if (logf)
        {
          (void) fprintf(logf, "poll interrupted by EINTR, returning MHD_YES\n");
          (void) fflush(logf);
        }
        result = MHD_YES;
        goto close_log;
      }
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("poll failed: %s\n"),
                MHD_socket_strerr_ (err));
#endif
      free (p);
      if (logf)
      {
        (void) fprintf(logf, "poll failed unrecoverable, returning MHD_NO\n");
        (void) fflush(logf);
      }
      result = MHD_NO;
      goto close_log;
    }

    if (logf)
    {
      (void) fprintf(logf, "poll succeeded, processing results\n");
      (void) fflush(logf);
    }

    /* handle ITC FD */
    /* do it before any other processing so
       new signals will be processed in next loop */
    if ( (-1 != poll_itc_idx) &&
         (0 != (p[poll_itc_idx].revents & POLLIN)) )
    {
      if (logf)
      {
        (void) fprintf(logf, "ITC fd ready at index %d\n", poll_itc_idx);
        (void) fflush(logf);
      }
      MHD_itc_clear_ (daemon->itc);
    }

    /* handle shutdown */
    if (daemon->shutdown)
    {
      free (p);
      if (logf)
      {
        (void) fprintf(logf, "daemon shutdown, returning MHD_NO\n");
        (void) fflush(logf);
      }
      result = MHD_NO;
      goto close_log;
    }

    /* Process externally added connection if any */
    if (daemon->have_new)
    {
      if (logf)
      {
        (void) fprintf(logf, "processing externally added connections\n");
        (void) fflush(logf);
      }
      new_connections_list_process_ (daemon);
    }

    /* handle 'listen' FD */
    if ( (-1 != poll_listen) &&
         (0 != (p[poll_listen].revents & POLLIN)) )
    {
      if (logf)
      {
        (void) fprintf(logf, "listen fd has POLLIN, accepting connection\n");
        (void) fflush(logf);
      }
      (void) MHD_accept_connection (daemon);
    }

    /* Reset. New value will be set when connections are processed. */
    daemon->data_already_pending = false;

    i = 0;
    prev = daemon->connections_tail;
    while (NULL != (pos = prev))
    {
      prev = pos->prev;
      /* first, sanity checks */
      if (i >= num_connections)
        break;     /* connection list changed somehow, retry later ... */
      if (p[poll_server + i].fd != pos->socket_fd)
        continue;  /* fd mismatch, something else happened, retry later ... */
      if (logf)
      {
        (void) fprintf(logf, "calling call_handlers for conn fd=%d index=%u revents=0x%x\n",
                       (int) pos->socket_fd, poll_server + i, (unsigned int) p[poll_server + i].revents);
        (void) fflush(logf);
      }
      call_handlers (pos,
                     0 != (p[poll_server + i].revents & POLLIN),
                     0 != (p[poll_server + i].revents & POLLOUT),
                     0 != (p[poll_server + i].revents
                           & MHD_POLL_REVENTS_ERR_DISC));
      i++;
    }
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
    for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
    {
      if (i >= num_connections)
        break;   /* connection list changed somehow, retry later ... */

      /* Get next connection here as connection can be removed
       * from 'daemon->urh_head' list. */
      urhn = urh->prev;
      /* Check for fd mismatch. FIXME: required for safety? */
      if ((p[poll_server + i].fd != urh->connection->socket_fd) ||
          (p[poll_server + i + 1].fd != urh->mhd.socket))
        break;
      urh_from_pollfd (urh,
                       &p[poll_server + i]);
      i += 2;
      process_urh (urh);
      /* Finished forwarding? */
      if ( (0 == urh->in_buffer_size) &&
           (0 == urh->out_buffer_size) &&
           (0 == urh->in_buffer_used) &&
           (0 == urh->out_buffer_used) )
      {
        /* MHD_connection_finish_forward_() will remove connection from
         * 'daemon->urh_head' list. */
        MHD_connection_finish_forward_ (urh->connection);
        urh->clean_ready = true;
        /* If 'urh->was_closed' already was set to true, connection will be
         * moved immediately to cleanup list. Otherwise connection
         * will stay in suspended list until 'urh' will be marked
         * with 'was_closed' by application. */
        MHD_resume_connection (urh->connection);
      }
    }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

    free (p);
    if (logf)
    {
      (void) fprintf(logf, "finished iteration, freeing pollfd array and returning MHD_YES\n");
      (void) fflush(logf);
    }
    result = MHD_YES;
    goto close_log;
  }

close_log:
  if (logf)
  {
    (void) fprintf(logf, "EXIT MHD_poll_all: result=%d\n", (int) result);
    (void) fflush(logf);
    (void) fclose(logf);
  }
  return result;
}
