static enum MHD_Result
MHD_poll_listen_socket (struct MHD_Daemon *daemon,
                        int may_block)
{
  struct pollfd p[2];
  int timeout;
  unsigned int poll_count;
  int poll_listen;
  int poll_itc_idx;
  MHD_socket ls;
  FILE *logf = NULL;
  int i;

  /* Open append log; if it fails, continue without logging to avoid
     changing behaviour. */
  logf = fopen("/tmp/logs/908.txt", "a");
  if (logf)
    {
      fprintf(logf, "ENTER MHD_poll_listen_socket: daemon=%p may_block=%d\n",
              (void *) daemon, may_block);
    }

  mhd_assert (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid));
  mhd_assert (MHD_thread_handle_ID_is_current_thread_ (daemon->tid));

  memset (&p,
          0,
          sizeof (p));
  if (logf)
    {
      fprintf(logf, "memset(&p,0,sizeof(p)) executed\n");
      for (i = 0; i < 2; ++i)
        fprintf(logf, "p[%d] after memset: fd=%d events=0x%x revents=0x%x\n",
                i, (int) p[i].fd, (unsigned int) p[i].events,
                (unsigned int) p[i].revents);
    }

  poll_count = 0;
  poll_listen = -1;
  poll_itc_idx = -1;
  if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
       (! daemon->was_quiesced) )

  {
    p[poll_count].fd = ls;
    p[poll_count].events = POLLIN;
    p[poll_count].revents = 0;
    poll_listen = (int) poll_count;
    if (logf)
      fprintf(logf, "added listen fd: ls=%d at index=%u -> poll_listen=%d\n",
              (int) ls, poll_count, poll_listen);
    poll_count++;
  }
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  {
    p[poll_count].fd = MHD_itc_r_fd_ (daemon->itc);
    p[poll_count].events = POLLIN;
    p[poll_count].revents = 0;
    poll_itc_idx = (int) poll_count;
    if (logf)
      fprintf(logf, "added itc fd: fd=%d at index=%u -> poll_itc_idx=%d\n",
              (int) p[poll_count].fd, poll_count, poll_itc_idx);
    poll_count++;
  }

  if (logf)
    {
      fprintf(logf, "poll_count after setup=%u poll_listen=%d poll_itc_idx=%d\n",
              poll_count, poll_listen, poll_itc_idx);
      for (i = 0; i < (int) poll_count; ++i)
        fprintf(logf, "p[%d] before poll: fd=%d events=0x%x revents=0x%x\n",
                i, (int) p[i].fd, (unsigned int) p[i].events,
                (unsigned int) p[i].revents);
    }

  if (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME))
    (void) resume_suspended_connections (daemon);

  if (MHD_NO == may_block)
    timeout = 0;
  else
    timeout = -1;
  if (0 == poll_count)
    {
      if (logf)
        {
          fprintf(logf, "early return: poll_count==0 -> returning MHD_YES\n");
          fclose(logf);
        }
      return MHD_YES;
    }
  if (MHD_sys_poll_ (p,
                     poll_count,
                     timeout) < 0)
  {
    const int err = MHD_socket_get_error_ ();

    if (logf)
      fprintf(logf, "MHD_sys_poll_ returned <0 err=%d\n", err);

    if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        if (logf)
          {
            fprintf(logf, "poll interrupted by EINTR -> returning MHD_YES\n");
            fclose(logf);
          }
        return MHD_YES;
      }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("poll failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    if (logf)
      {
        fprintf(logf, "poll failed (non-EINTR) -> returning MHD_NO\n");
        fclose(logf);
      }
    return MHD_NO;
  }

  if (logf)
    {
      fprintf(logf, "MHD_sys_poll_ succeeded; poll results:\n");
      for (i = 0; i < (int) poll_count; ++i)
        fprintf(logf, "p[%d] after poll: fd=%d events=0x%x revents=0x%x\n",
                i, (int) p[i].fd, (unsigned int) p[i].events,
                (unsigned int) p[i].revents);
    }

  if ( (0 <= poll_itc_idx) &&
       (0 != (p[poll_itc_idx].revents & POLLIN)) )
    {
      if (logf)
        fprintf(logf, "itc index %d has POLLIN; clearing itc\n", poll_itc_idx);
      MHD_itc_clear_ (daemon->itc);
    }

  /* handle shutdown */
  if (daemon->shutdown)
    {
      if (logf)
        {
          fprintf(logf, "daemon->shutdown true -> returning MHD_NO\n");
          fclose(logf);
        }
      return MHD_NO;
    }

  /* Process externally added connection if any */
  if (daemon->have_new)
    {
      if (logf)
        fprintf(logf, "daemon->have_new true -> processing new connections\n");
      new_connections_list_process_ (daemon);
    }

  if ( (0 <= poll_listen) &&
       (0 != (p[poll_listen].revents & POLLIN)) )
    {
      if (logf)
        fprintf(logf, "listen index %d has POLLIN -> accepting connection\n",
                poll_listen);
      (void) MHD_accept_connection (daemon);
    }

  if (logf)
    {
      fprintf(logf, "EXIT MHD_poll_listen_socket -> returning MHD_YES\n");
      fclose(logf);
    }
  return MHD_YES;
}
