void
MHD_pool_deallocate (struct MemoryPool *pool,
                     void *block,
                     size_t block_size)
{
  /* Probe: log entry and initial pool/block state */
  {
    FILE *log = fopen("/tmp/logs/3983.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "ENTER MHD_pool_deallocate: pool=%p, memory=%p, size=%zu, pos=%zu, end=%zu, block=%p, block_size=%zu\n",
              (void *)pool,
              (void *)(pool ? pool->memory : NULL),
              (size_t)(pool ? pool->size : 0),
              (size_t)(pool ? pool->pos : 0),
              (size_t)(pool ? pool->end : 0),
              block,
              block_size);
      fclose(log);
    }
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (block != NULL || block_size == 0);
  mhd_assert (pool->size >= block_size);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));

  if (NULL != block)
  {   /* Have previously allocated data */
    const size_t block_offset = mp_ptr_diff_ (block, pool->memory);

    /* Probe: log computed block_offset and pointers */
    {
      FILE *log = fopen("/tmp/logs/3983.txt", "a");
      if (NULL != log)
      {
        void *block_end = (void *)((char *)block + block_size);
        void *pool_end_ptr = (void *)((char *)pool->memory + pool->size);
        fprintf(log, "BLOCK INFO: block=%p, block_size=%zu, block_offset=%zu, block_end=%p, pool_memory=%p, pool_size=%zu, pool_end_ptr=%p, pool_pos=%zu, pool_end=%zu\n",
                block, block_size, block_offset,
                block_end,
                (void *)pool->memory, pool->size,
                pool_end_ptr,
                pool->pos, pool->end);
        fclose(log);
      }
    }

    mhd_assert (mp_ptr_le_ (pool->memory, block));
    mhd_assert (block_offset <= pool->size);
    mhd_assert ((block_offset != pool->pos) || (block_size == 0));
    /* Zero-out deallocated region */
    if (0 != block_size)
    {
      /* Probe: log before memset and poison */
      {
        FILE *log = fopen("/tmp/logs/3983.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "ABOUT TO memset: block=%p, block_size=%zu\n", block, block_size);
          fclose(log);
        }
      }

      memset (block, 0, block_size);

      /* Probe: log after memset and before poison */
      {
        FILE *log = fopen("/tmp/logs/3983.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "AFTER memset: block=%p, block_size=%zu. Calling _MHD_POISON_MEMORY.\n",
                  block, block_size);
          fclose(log);
        }
      }

      _MHD_POISON_MEMORY (block, block_size);
    }
#if ! defined(MHD_FAVOR_SMALL_CODE) && ! defined(MHD_ASAN_POISON_ACTIVE)
    else
    {
      /* Probe: zero size early return */
      FILE *log = fopen("/tmp/logs/3983.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "ZERO-SIZE: block=%p, returning early.\n", block);
        fclose(log);
      }
      return; /* Zero size, no need to do anything */
    }
#endif /* ! MHD_FAVOR_SMALL_CODE && ! MHD_ASAN_POISON_ACTIVE */
    if (block_offset <= pool->pos)
    {
      /* "Normal" block, not allocated "from the end". */
      const size_t alg_end =
        ROUND_TO_ALIGN_PLUS_RED_ZONE (block_offset + block_size);

      /* Probe: log alg_end and comparison with pool->pos */
      {
        FILE *log = fopen("/tmp/logs/3983.txt", "a");
        if (NULL != log)
        {
          fprintf(log, "NORMAL BLOCK PATH: block_offset=%zu, block_size=%zu, alg_end=%zu, pool_pos=%zu\n",
                  block_offset, block_size, alg_end, pool->pos);
          fclose(log);
        }
      }

      mhd_assert (alg_end <= pool->pos);
      if (alg_end == pool->pos)
      {
        /* The last allocated block, return deallocated block to the pool */
        size_t alg_start = ROUND_TO_ALIGN (block_offset);
        mhd_assert (alg_start >= block_offset);
#if defined(MHD_ASAN_POISON_ACTIVE)
        if (alg_start != block_offset)
        {
          _MHD_POISON_MEMORY (pool->memory + block_offset, \
                              alg_start - block_offset);
        }
        else if (0 != alg_start)
        {
          bool need_red_zone_before;
          mhd_assert (_MHD_RED_ZONE_SIZE <= alg_start);
#if defined(HAVE___ASAN_REGION_IS_POISONED)
          need_red_zone_before =
            (NULL == __asan_region_is_poisoned (pool->memory
                                                + alg_start
                                                - _MHD_RED_ZONE_SIZE,
                                                _MHD_RED_ZONE_SIZE));
#elif defined(HAVE___ASAN_ADDRESS_IS_POISONED)
          need_red_zone_before =
            (0 == __asan_address_is_poisoned (pool->memory + alg_start - 1));
#else  /* ! HAVE___ASAN_ADDRESS_IS_POISONED */
          need_red_zone_before = true; /* Unknown, assume new red zone needed */
#endif /* ! HAVE___ASAN_ADDRESS_IS_POISONED */
          if (need_red_zone_before)
          {
            _MHD_POISON_MEMORY (pool->memory + alg_start, _MHD_RED_ZONE_SIZE);
            alg_start += _MHD_RED_ZONE_SIZE;
          }
        }
#endif /* MHD_ASAN_POISON_ACTIVE */
        mhd_assert (alg_start <= pool->pos);
        mhd_assert (alg_start == ROUND_TO_ALIGN (alg_start));

        /* Probe: log alg_start and new pool->pos about to be set */
        {
          FILE *log = fopen("/tmp/logs/3983.txt", "a");
          if (NULL != log)
          {
            fprintf(log, "RETURN TO POOL: alg_start=%zu, old_pool_pos=%zu -> new_pool_pos=%zu\n",
                    alg_start, pool->pos, alg_start);
            fclose(log);
          }
        }

        pool->pos = alg_start;
      }
    }
    else
    {
      /* Allocated "from the end" block. */
      /* The size and the pointers of such block should not be manipulated by
         MHD code (block split is disallowed). */
      mhd_assert (block_offset >= pool->end);
      mhd_assert (ROUND_TO_ALIGN (block_offset) == block_offset);
      if (block_offset == pool->end)
      {
        /* The last allocated block, return deallocated block to the pool */
        const size_t alg_end =
          ROUND_TO_ALIGN_PLUS_RED_ZONE (block_offset + block_size);

        /* Probe: log end-allocated block path and values */
        {
          FILE *log = fopen("/tmp/logs/3983.txt", "a");
          if (NULL != log)
          {
            fprintf(log, "END-ALLOCATED PATH: block_offset=%zu, block_size=%zu, alg_end=%zu, old_pool_end=%zu -> new_pool_end=%zu\n",
                    block_offset, block_size, alg_end, pool->end, alg_end);
            fclose(log);
          }
        }

        pool->end = alg_end;
      }
    }
  }

  /* Probe: final state */
  {
    FILE *log = fopen("/tmp/logs/3983.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "EXIT MHD_pool_deallocate: pool=%p, memory=%p, size=%zu, pos=%zu, end=%zu\n",
              (void *)pool,
              (void *)(pool ? pool->memory : NULL),
              (size_t)(pool ? pool->size : 0),
              (size_t)(pool ? pool->pos : 0),
              (size_t)(pool ? pool->end : 0));
      fclose(log);
    }
  }
}
