size_t
MHD_pool_get_free (struct MemoryPool *pool)
{
  /* Logging: capture entry and key state to help reproduce Rule 8.13 scenario */
  {
    FILE *log = fopen("/tmp/logs/2125.txt", "a");
    if (log != NULL)
      {
        fprintf(log, "MHD_pool_get_free entered: pool=%p\n", (void *)pool);
        /* Defensive printing of members (assumes members are size_t or compatible) */
        fprintf(log, "pool->end=%zu, pool->pos=%zu, pool->size=%zu\n",
                (size_t)pool->end, (size_t)pool->pos, (size_t)pool->size);
        {
          size_t diff = (size_t)(pool->end - pool->pos);
          fprintf(log, "computed end-pos = %zu\n", diff);
          fprintf(log, "check end>=pos: %s\n", (pool->end >= pool->pos) ? "true" : "false");
          fprintf(log, "check size>=end-pos: %s\n", (pool->size >= diff) ? "true" : "false");
#ifdef ROUND_TO_ALIGN
          fprintf(log, "ROUND_TO_ALIGN(pos) = %zu\n", (size_t)ROUND_TO_ALIGN(pool->pos));
          fprintf(log, "check pos==ROUND_TO_ALIGN(pos): %s\n", (pool->pos == ROUND_TO_ALIGN(pool->pos)) ? "true" : "false");
#else
          fprintf(log, "ROUND_TO_ALIGN macro not available in this build; pos=%zu\n", (size_t)pool->pos);
#endif
        }
        fclose(log);
      }
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));
#ifdef MHD_ASAN_POISON_ACTIVE
  if ((pool->end - pool->pos) <= _MHD_RED_ZONE_SIZE)
    {
      FILE *log = fopen("/tmp/logs/2125.txt", "a");
      if (log != NULL)
        {
          fprintf(log, "ASAN branch: (end-pos)=%zu <= _MHD_RED_ZONE_SIZE=%zu -> returning 0\n",
                  (size_t)(pool->end - pool->pos), (size_t)_MHD_RED_ZONE_SIZE);
          fclose(log);
        }
      return 0;
    }
#endif /* MHD_ASAN_POISON_ACTIVE */
  {
    size_t result = (size_t)((pool->end - pool->pos) - _MHD_RED_ZONE_SIZE);
    FILE *log = fopen("/tmp/logs/2125.txt", "a");
    if (log != NULL)
      {
        fprintf(log, "MHD_pool_get_free returning: %zu\n", result);
        fclose(log);
      }
    return result;
  }
}
