size_t
MHD_pool_get_free (struct MemoryPool *pool)
{
  /* Probe: log entry and relevant pool state for debugging/coverage */
  FILE *log = NULL;
  log = fopen("/tmp/logs/2125.txt", "a");
  if (log != NULL)
    {
      /* Print pointer and fields observed (use casts where appropriate) */
      (void) fprintf(log, "MHD_pool_get_free ENTRY: pool=%p end=%zu pos=%zu size=%zu\n",
                     (void *) pool,
                     (size_t) pool->end,
                     (size_t) pool->pos,
                     (size_t) pool->size);
      (void) fflush(log);
    }

  /* Preserve original assertions */
  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));

#ifdef MHD_ASAN_POISON_ACTIVE
  {
    size_t diff = (size_t) (pool->end - pool->pos);
    if (log != NULL)
      {
        (void) fprintf(log, "MHD_ASAN_POISON_ACTIVE: diff=%zu _MHD_RED_ZONE_SIZE=%zu\n",
                       diff, (size_t) _MHD_RED_ZONE_SIZE);
        (void) fflush(log);
      }
    if (diff <= _MHD_RED_ZONE_SIZE)
      {
        if (log != NULL)
          {
            (void) fprintf(log, "MHD_ASAN_POISON_ACTIVE: branch taken -> return 0\n");
            (void) fclose(log);
          }
        return 0;
      }
  }
#endif /* MHD_ASAN_POISON_ACTIVE */

  {
    size_t free_space = (size_t) (pool->end - pool->pos) - (size_t) _MHD_RED_ZONE_SIZE;
    if (log != NULL)
      {
        (void) fprintf(log, "MHD_pool_get_free EXIT: free=%zu\n", free_space);
        (void) fclose(log);
      }
    return free_space;
  }
}
