#include <stdio.h>
#include <stddef.h>

size_t
MHD_pool_get_free (struct MemoryPool *pool)
{
  /* Log entry and state before assertions so failures can be diagnosed. */
  FILE *logf = fopen("/tmp/logs/2125.txt", "a");
  if (logf)
    {
      fprintf(logf, "MHD_pool_get_free ENTER: pool=%p\n", (void *)pool);
      /* Print member values; cast to size_t for portable printing. */
      fprintf(logf, "  pool->size = %zu\n", (size_t)pool->size);
      fprintf(logf, "  pool->end  = %zu\n", (size_t)pool->end);
      fprintf(logf, "  pool->pos  = %zu\n", (size_t)pool->pos);
      /* Also log the alignment of pos as used in the assertion. */
      fprintf(logf, "  ROUND_TO_ALIGN(pool->pos) = %zu\n", (size_t)ROUND_TO_ALIGN (pool->pos));
      fclose(logf);
    }

  /* Preserve original assertions and logic. */
  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (pool->pos == ROUND_TO_ALIGN (pool->pos));

#ifdef MHD_ASAN_POISON_ACTIVE
  {
    size_t diff = pool->end - pool->pos;
    /* Log decision for ASAN branch. */
    FILE *logf2 = fopen("/tmp/logs/2125.txt", "a");
    if (logf2)
      {
        fprintf(logf2, "  ASAN branch active: end - pos = %zu, _MHD_RED_ZONE_SIZE = %zu\n",
                (size_t)diff, (size_t)_MHD_RED_ZONE_SIZE);
        if (diff <= _MHD_RED_ZONE_SIZE)
          fprintf(logf2, "  ASAN: returning 0 due to red zone\n");
        else
          fprintf(logf2, "  ASAN: proceeding to compute free space\n");
        fclose(logf2);
      }
    if (diff <= _MHD_RED_ZONE_SIZE)
      return 0;
  }
#endif /* MHD_ASAN_POISON_ACTIVE */

  {
    size_t result = (pool->end - pool->pos) - _MHD_RED_ZONE_SIZE;
    FILE *logf3 = fopen("/tmp/logs/2125.txt", "a");
    if (logf3)
      {
        fprintf(logf3, "  returning free = %zu\n", (size_t)result);
        fprintf(logf3, "MHD_pool_get_free EXIT: pool=%p\n", (void *)pool);
        fclose(logf3);
      }
    return result;
  }
}
