bool
MHD_pool_is_resizable_inplace (struct MemoryPool *pool,
                               void *block,
                               size_t block_size)
{
  /* Attempt to log control flow and key state so agents can reproduce
     and gather evidence for the pointer const-qualification issue. */
  FILE *logf = fopen("/tmp/logs/3815.txt", "a");
  if (NULL == logf)
  {
    /* Fallback if target dir doesn't exist */
    logf = fopen("/tmp/3815.txt", "a");
  }
  if (logf)
  {
    fprintf(logf, "ENTER MHD_pool_is_resizable_inplace: pool=%p, block=%p, block_size=%zu\n",
            (void *) pool, block, block_size);
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (block != NULL || block_size == 0);
  mhd_assert (pool->size >= block_size);
  if (NULL != block)
  {
    const size_t block_offset = mp_ptr_diff_ (block, pool->memory);
    if (logf)
    {
      fprintf(logf, "BLOCK NON-NULL: block_offset=%zu, pool->memory=%p\n",
              block_offset, (void *) pool->memory);
    }
    mhd_assert (mp_ptr_le_ (pool->memory, block));
    mhd_assert (pool->size >= block_offset);
    mhd_assert (pool->size >= block_offset + block_size);

    const size_t rounded = ROUND_TO_ALIGN_PLUS_RED_ZONE (block_offset + block_size);
    if (logf)
    {
      fprintf(logf, "pool->pos=%zu, rounded=%zu, equality=(%d)\n",
              pool->pos, rounded, (int) (pool->pos == rounded));
    }
    const bool result = (pool->pos == rounded);
    if (logf)
    {
      fprintf(logf, "EXIT MHD_pool_is_resizable_inplace (non-null): %d\n", (int) result);
      fclose(logf);
    }
    return result;
  }

  if (logf)
  {
    fprintf(logf, "EXIT MHD_pool_is_resizable_inplace (null block): false\n");
    fclose(logf);
  }
  return false; /* Unallocated blocks cannot be resized in-place */
}
