bool
MHD_pool_is_resizable_inplace (struct MemoryPool *pool,
                               void *block,
                               size_t block_size)
{
  /* Open log file for appending; best-effort logging, do not fail if logging
     is not possible. */
  FILE *logf = fopen("/tmp/logs/3815.txt", "a");
  if (logf)
  {
    fprintf(logf, "ENTER MHD_pool_is_resizable_inplace: pool=%p, block=%p, block_size=%zu\n",
            (void *)pool, block, block_size);
    /* Print basic pool state to aid in reproducing the violation scenario. */
    fprintf(logf, "pool->memory=%p, pool->pos=%zu, pool->end=%zu, pool->size=%zu\n",
            (void *)pool->memory, pool->pos, pool->end, pool->size);
    fflush(logf);
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (block != NULL || block_size == 0);
  mhd_assert (pool->size >= block_size);
  if (NULL != block)
  {
    const size_t block_offset = mp_ptr_diff_ (block, pool->memory);
    if (logf)
    {
      fprintf(logf, "Computed block_offset=%zu (mp_ptr_diff_(block=%p, pool->memory=%p))\n",
              block_offset, block, (void *)pool->memory);
      fprintf(logf, "mp_ptr_le_(pool->memory, block) -> %d\n",
              (int) mp_ptr_le_ (pool->memory, block));
      fprintf(logf, "Checks: pool->size >= block_offset -> %d, pool->size >= block_offset + block_size -> %d\n",
              (int) (pool->size >= block_offset),
              (int) (pool->size >= block_offset + block_size));
      fflush(logf);
    }

    mhd_assert (mp_ptr_le_ (pool->memory, block));
    mhd_assert (pool->size >= block_offset);
    mhd_assert (pool->size >= block_offset + block_size);

    const bool res = (pool->pos ==
                      ROUND_TO_ALIGN_PLUS_RED_ZONE (block_offset + block_size));
    if (logf)
    {
      fprintf(logf, "Result check: pool->pos=%zu, ROUND_TO_ALIGN_PLUS_RED_ZONE(block_offset+block_size)=%zu, res=%d\n",
              pool->pos,
              (size_t) ROUND_TO_ALIGN_PLUS_RED_ZONE (block_offset + block_size),
              (int) res);
      fclose(logf);
    }
    return res;
  }

  if (logf)
  {
    fprintf(logf, "Unallocated block (block == NULL) -> returning false\n");
    fclose(logf);
  }
  return false; /* Unallocated blocks cannot be resized in-place */
}
