void *
MHD_pool_reset (struct MemoryPool *pool,
                void *keep,
                size_t copy_bytes,
                size_t new_size)
{
  /* Logging probe: open log file and record entry and key state */
  FILE *mhd_log_fp_3635 = fopen("/tmp/logs/3635.txt", "a");
  if (mhd_log_fp_3635)
  {
    fprintf(mhd_log_fp_3635, "MHD_pool_reset ENTRY: pool=%p, pool->memory=%p, pool->size=%zu, pool->pos=%zu, pool->end=%zu, keep=%p, copy_bytes=%zu, new_size=%zu\n",
            (void *)pool,
            (void *)pool->memory,
            (size_t)pool->size,
            (size_t)pool->pos,
            (size_t)pool->end,
            (void *)keep,
            (size_t)copy_bytes,
            (size_t)new_size);
    fflush(mhd_log_fp_3635);
  }

  mhd_assert (pool->end >= pool->pos);
  mhd_assert (pool->size >= pool->end - pool->pos);
  mhd_assert (copy_bytes <= new_size);
  mhd_assert (copy_bytes <= pool->size);
  mhd_assert (keep != NULL || copy_bytes == 0);
  mhd_assert (keep == NULL || mp_ptr_le_ (pool->memory, keep));
  /* (keep == NULL || pool->memory + pool->size >= (uint8_t*) keep + copy_bytes) */
  mhd_assert ((keep == NULL) || \
              (pool->size >= mp_ptr_diff_ (keep, pool->memory) + copy_bytes));
#if defined(MHD_ASAN_POISON_ACTIVE) && defined(HAVE___ASAN_REGION_IS_POISONED)
  mhd_assert (NULL == __asan_region_is_poisoned (keep, copy_bytes));
#endif /* MHD_ASAN_POISON_ACTIVE && HAVE___ASAN_REGION_IS_POISONED */

  if (mhd_log_fp_3635)
  {
    fprintf(mhd_log_fp_3635, "After asserts: verifying conditions: keep_is_null=%d, keep_is_pool_memory=%d, copy_bytes_zero=%d\n",
            (keep == NULL) ? 1 : 0,
            (keep == pool->memory) ? 1 : 0,
            (copy_bytes == 0) ? 1 : 0);
    fflush(mhd_log_fp_3635);
  }

  _MHD_UNPOISON_MEMORY (pool->memory, new_size);

  if ( (NULL != keep) &&
       (keep != pool->memory) )
  {
    if (mhd_log_fp_3635)
    {
      fprintf(mhd_log_fp_3635, "About to possibly memmove: keep=%p, pool->memory=%p, copy_bytes=%zu\n",
              (void *)keep,
              (void *)pool->memory,
              (size_t)copy_bytes);
      fflush(mhd_log_fp_3635);
    }

    if (0 != copy_bytes)
    {
      memmove (pool->memory,
               keep,
               copy_bytes);
      if (mhd_log_fp_3635)
      {
        fprintf(mhd_log_fp_3635, "Performed memmove of %zu bytes from %p to %p\n",
                (size_t)copy_bytes,
                (void *)keep,
                (void *)pool->memory);
        fflush(mhd_log_fp_3635);
      }
    }
    else
    {
      if (mhd_log_fp_3635)
      {
        fprintf(mhd_log_fp_3635, "Skipped memmove because copy_bytes == 0\n");
        fflush(mhd_log_fp_3635);
      }
    }
  }
  else
  {
    if (mhd_log_fp_3635)
    {
      fprintf(mhd_log_fp_3635, "Skipping memmove branch: keep==NULL or keep==pool->memory (keep=%p, pool->memory=%p)\n",
              (void *)keep,
              (void *)pool->memory);
      fflush(mhd_log_fp_3635);
    }
  }

  /* technically not needed, but safer to zero out */
  if (pool->size > copy_bytes)
  {
    size_t to_zero;   /** Size of area to zero-out */

    to_zero = pool->size - copy_bytes;
    _MHD_UNPOISON_MEMORY (pool->memory + copy_bytes, to_zero);

    if (mhd_log_fp_3635)
    {
      fprintf(mhd_log_fp_3635, "Zero-out branch: pool->size=%zu, copy_bytes=%zu, to_zero=%zu\n",
              (size_t)pool->size,
              (size_t)copy_bytes,
              (size_t)to_zero);
      fflush(mhd_log_fp_3635);
    }

#ifdef _WIN32
    if (pool->is_mmap)
    {
      size_t to_recommit;     /** Size of decommitted and re-committed area. */
      uint8_t *recommit_addr;
      /* Round down to page size */
      to_recommit = to_zero - to_zero % MHD_sys_page_size_;
      recommit_addr = pool->memory + pool->size - to_recommit;

      if (mhd_log_fp_3635)
      {
        fprintf(mhd_log_fp_3635, "Windows mmap branch: is_mmap=1, to_recommit=%zu, recommit_addr=%p\n",
                (size_t)to_recommit,
                (void *)recommit_addr);
        fflush(mhd_log_fp_3635);
      }

      /* De-committing and re-committing again clear memory and make
       * pages free / available for other needs until accessed. */
      if (VirtualFree (recommit_addr,
                       to_recommit,
                       MEM_DECOMMIT))
      {
        to_zero -= to_recommit;

        if (recommit_addr != VirtualAlloc (recommit_addr,
                                           to_recommit,
                                           MEM_COMMIT,
                                           PAGE_READWRITE))
          abort ();      /* Serious error, must never happen */

        if (mhd_log_fp_3635)
        {
          fprintf(mhd_log_fp_3635, "VirtualFree/VirtualAlloc succeeded, remaining to_zero=%zu\n",
                  (size_t)to_zero);
          fflush(mhd_log_fp_3635);
        }
      }
      else
      {
        if (mhd_log_fp_3635)
        {
          fprintf(mhd_log_fp_3635, "VirtualFree failed for recommit_addr=%p, to_recommit=%zu\n",
                  (void *)recommit_addr,
                  (size_t)to_recommit);
          fflush(mhd_log_fp_3635);
        }
      }
    }
#endif /* _WIN32 */

    memset (&pool->memory[copy_bytes],
            0,
            to_zero);

    if (mhd_log_fp_3635)
    {
      fprintf(mhd_log_fp_3635, "Completed memset of %zu bytes at %p (addr = %p)\n",
              (size_t)to_zero,
              (void *)&pool->memory[copy_bytes],
              (void *)(pool->memory + copy_bytes));
      fflush(mhd_log_fp_3635);
    }
  }

  pool->pos = ROUND_TO_ALIGN_PLUS_RED_ZONE (new_size);
  pool->end = pool->size;
  _MHD_POISON_MEMORY (((uint8_t *) pool->memory) + new_size, \
                      pool->size - new_size);

  if (mhd_log_fp_3635)
  {
    fprintf(mhd_log_fp_3635, "Leaving MHD_pool_reset: pool->pos=%zu, pool->end=%zu, returning %p\n",
            (size_t)pool->pos,
            (size_t)pool->end,
            (void *)pool->memory);
    fclose(mhd_log_fp_3635);
  }

  return pool->memory;
}
