_MHD_EXTERN enum MHD_Result
MHD_post_process (struct MHD_PostProcessor *pp,
                  const char *post_data,
                  size_t post_data_len)
{
  FILE *log = fopen("/tmp/logs/1443.txt", "a");
  if (log)
    fprintf(log, "MHD_post_process entered: post_data_len=%zu, pp=%p\n", post_data_len, (void *)pp);

  if (0 == post_data_len) {
    if (log) {
      fprintf(log, "early return: post_data_len == 0 -> MHD_YES\n");
      fclose(log);
    }
    return MHD_YES;
  }

  if (NULL == pp) {
    if (log) {
      fprintf(log, "early return: pp == NULL -> MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }

  if (log)
    fprintf(log, "pp != NULL; pp->encoding=%p\n", (void *)pp->encoding);

  /* First comparison: form-urlencoded */
  if (log) {
    size_t n = MHD_STATICSTR_LEN_(MHD_HTTP_POST_ENCODING_FORM_URLENCODED);
    fprintf(log, "Calling MHD_str_equal_caseless_n_ (FORM_URLENCODED) with encoding=%p, n=%zu\n",
            (void *)pp->encoding, n);
    fflush(log);
  }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_FORM_URLENCODED))) {
    if (log) {
      fprintf(log, "Matched FORM_URLENCODED -> calling post_process_urlencoded\n");
      fflush(log);
    }
    enum MHD_Result res = post_process_urlencoded (pp,
                                                   post_data,
                                                   post_data_len);
    if (log) {
      fprintf(log, "post_process_urlencoded returned=%d\n", (int)res);
      fclose(log);
    }
    return res;
  }

  /* Second comparison: multipart/form-data */
  if (log) {
    size_t n = MHD_STATICSTR_LEN_(MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA);
    fprintf(log, "Calling MHD_str_equal_caseless_n_ (MULTIPART_FORMDATA) with encoding=%p, n=%zu\n",
            (void *)pp->encoding, n);
    fflush(log);
  }

  if (MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                 pp->encoding,
                                 MHD_STATICSTR_LEN_ (
                                   MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA))) {
    if (log) {
      fprintf(log, "Matched MULTIPART_FORMDATA -> calling post_process_multipart\n");
      fflush(log);
    }
    enum MHD_Result res = post_process_multipart (pp,
                                                  post_data,
                                                  post_data_len);
    if (log) {
      fprintf(log, "post_process_multipart returned=%d\n", (int)res);
      fclose(log);
    }
    return res;
  }

  if (log) {
    fprintf(log, "No encoding match found -> return MHD_NO (this should never be reached)\n");
    fclose(log);
  }

  /* this should never be reached */
  return MHD_NO;
}
